-- Table structure for `sp_news1111`
-- ----------------------------
DROP TABLE IF EXISTS `sp_news1111`;
CREATE TABLE `sp_news1111` (
  `n_id` int(4) NOT NULL AUTO_INCREMENT,
  `n_title` varchar(255) NOT NULL,
  `n_text` text NOT NULL,
  `n_keyword` varchar(255) DEFAULT NULL,
  `n_pics` varchar(255) DEFAULT NULL,
  `n_type` int(4) NOT NULL COMMENT '1，集团新闻；2行业新闻，3媒体关注',
  `n_intro` text,
  `n_time` int(11) NOT NULL,
  `n_state` int(4) NOT NULL DEFAULT '1',
  `n_pics_text` varchar(255) DEFAULT NULL,
  `is_top` tinyint(2) NOT NULL DEFAULT '0' COMMENT '1为全表头条，0为不是',
  `is_itop` tinyint(2) NOT NULL DEFAULT '0' COMMENT '1为子类头条，0为不是。',
  PRIMARY KEY (`n_id`),
  KEY `n_type` (`n_type`,`n_state`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=85 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of sp_news1111
-- ----------------------------
INSERT INTO `sp_news1111` VALUES ('30', '成都市武侯区火车南站街道办事处到巴蜀画派集团公司调研', '<p style=\"margin-left:0cm;text-indent:48.0pt;\" align=\"center\">\r\n	<span><img src=\"/Public/image/20160826/20160826092644_22082.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"margin-left:0cm;text-indent:48.0pt;\" align=\"center\">\r\n	<span><b><span style=\"font-size:12.0pt;font-family:宋体;\">调研现场 图<span>1</span></span></b><br />\r\n</span> \r\n</p>\r\n<p style=\"margin-left:0cm;text-indent:48.0pt;\">\r\n	<span>8</span>月<span>3</span>日下午，成都市武侯区火车南站街道办事处党工委书记李春绪、办事处主任孙蓓等一行到巴蜀画派集团公司调研，了解企业发展情况，对接支持服务内容，巴蜀画派集团公司董事长李杰介绍有关情况。\r\n</p>\r\n<p style=\"margin-left:0cm;text-indent:48.0pt;\">\r\n	<img src=\"/Public/image/20160826/20160826092729_14147.jpg\" alt=\"\" height=\"600\" width=\"800\" /><br />\r\n<span></span> \r\n</p>\r\n<div align=\"center\">\r\n	<span><b><span style=\"font-size:12.0pt;font-family:宋体;\">调研现场 图<span>2</span></span></b></span><br />\r\n</div>', null, null, '1', '8月3日下午，成都市武侯区火车南站街道办事处党工委书记李春绪、办事处主任孙蓓等一行到巴蜀画派集团公司调研，了解企业发展情况，对接支持服务内容，巴蜀画派集团公司董事长李杰介绍有关情况。', '1431273600', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('31', '著名旅京画家罗云到访巴蜀画派集团公司', '<div align=\"center\">\r\n	<span style=\"font-family:宋体;font-size:21px;\"> \r\n	<p style=\"margin-left:0cm;text-indent:48.0pt;\">\r\n		<span><img src=\"/Public/image/20160826/20160826093529_59724.jpg\" alt=\"\" /></span> \r\n	</p>\r\n</span> \r\n</div>\r\n<div align=\"center\">\r\n	<b><span style=\"font-size:12.0pt;font-family:宋体;\">拜访现场图<span>1</span></span></b><br />\r\n<span style=\"font-family:宋体;font-size:21px;\"> </span> \r\n</div>\r\n<span style=\"font-family:宋体;font-size:21px;\"> \r\n<p style=\"margin-left:0cm;text-indent:48.0pt;\">\r\n	<span></span> \r\n</p>\r\n</span><span style=\"font-family:宋体;font-size:21px;\"> \r\n<p style=\"margin-left:0cm;text-indent:48.0pt;\">\r\n	<span>8</span>月<span>2</span>日下午，巴蜀画派促进会副会长、著名旅京画家罗云，画家赵德兰到访巴蜀画派集团公司，和集团公司董事长李杰友好交谈，共叙友谊，共话艺术。\r\n</p>\r\n<br />\r\n</span> \r\n<div align=\"center\">\r\n	<span style=\"font-family:宋体;font-size:21px;\"> </span><img src=\"/Public/image/20160826/20160826093549_75085.jpg\" alt=\"\" /><b><span style=\"font-size:12.0pt;font-family:宋体;\">拜访现场图<span>2</span></span></b> \r\n</div>', null, null, '1', '8月31日下午，巴蜀画派促进会副会长、著名旅京画家罗云，画家赵德兰到访巴蜀画派集团公司，和集团公司董事长李杰友好交谈，共叙友谊，共话艺术。', '1431100800', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('32', '武侯区委常委、宣传部长张斌等领导到巴蜀画派集团公司调研', '<b><br />\r\n</b> \r\n<div align=\"center\">\r\n	<b> </b><img src=\"/Public/image/20160826/20160826095613_60028.jpg\" alt=\"\" /> \r\n</div>\r\n<p style=\"margin-left:0cm;text-indent:48.0pt;\">\r\n	<span>8</span>月<span>23</span>日上午，武侯区委常委、宣传部长张斌等领导到巴蜀画派集团公司调研指导工作。<span></span> \r\n</p>\r\n<p style=\"margin-left:0cm;text-indent:48.0pt;\">\r\n	在访问过程中，领导高度评价了巴蜀画派集团这些年的努力和工作。并要求、鼓励集团有跟好的发展，致力于将中国传统文化发扬下去。\r\n</p>\r\n<p style=\"margin-left:0cm;text-indent:48.0pt;\">\r\n	<img src=\"/Public/image/20160826/20160826094634_16654.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"margin-left:0cm;text-indent:48.0pt;\" align=\"center\">\r\n	<b><span style=\"font-size:12.0pt;font-family:宋体;\"><span></span></span></b> \r\n</p>', null, null, '1', '8月23日上午，武侯区委常委、宣传部长张斌等领导到巴蜀画派集团公司调研指导工作。在访问过程中，领导高度评价了巴蜀画派集团这些年的努力和工作。并要求、鼓励集团有跟好的发展，致力于将中国传统文化发扬下去。', '1431014400', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('34', '巴蜀画派文化产业集团董事长李杰天府新区煎茶（镇）街道办事处考察', '<p style=\"margin-left:0cm;text-indent:48.0pt;\">\r\n	<span>7</span>月<span>22</span>日考察天府新区煎茶（镇）街道办事处。天府新区，未来之都。成都科学城，成都未来的“天府广场”<span>----</span>成都之心。感谢煎茶党委书记景波、主任董永以及副主任、党委委员等支持巴蜀画派集团产业发展。\r\n</p>\r\n<p style=\"margin-left:0cm;text-indent:48.0pt;\">\r\n	<img src=\"/Public/image/20160826/20160826102542_20717.png\" alt=\"\" /><br />\r\n<span></span>\r\n</p>', null, null, '2', '7月22日考察天府新区煎茶（镇）街道办事处。天府新区，未来之都。成都科学城，成都未来的“天府广场”----成都之心。感谢煎茶党委书记景波、主任董永以及副主任、党委委员等支持巴蜀画派集团产业发展。', '1430928000', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('33', '成都市武侯区火车南站街道办事处到巴蜀画派集团公司调研', '<p style=\"margin-left:0cm;text-indent:48.0pt;\">\r\n	<span>8</span>月<span>3</span>日下午，成都市武侯区火车南站街道办事处党工委书记李春绪、办事处主任孙蓓等一行到巴蜀画派集团公司调研，了解企业发展情况，对接支持服务内容，巴蜀画派集团公司董事长李杰介绍有关情况。\r\n</p>\r\n<p style=\"margin-left:0cm;text-indent:48.0pt;\">\r\n	<img src=\"/Public/image/20160826/20160826095840_75972.jpg\" alt=\"\" /><img src=\"/Public/image/20160826/20160826095857_51827.jpg\" alt=\"\" /><img src=\"/Public/image/20160826/20160826095918_28594.jpg\" alt=\"\" /><br />\r\n<span></span> \r\n</p>', null, null, '1', '8月5日下午，成都市武侯区火车南站街道办事处党工委书记李春绪、办事处主任孙蓓等一行到巴蜀画派集团公司调研，了解企业发展情况，对接支持服务内容，巴蜀画派集团公司董事长李杰介绍有关情况', '1430841600', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('35', '巴蜀画派文化产业集团董事长李杰乐山考察', '<p style=\"margin-left:0cm;text-indent:48.0pt;\">\r\n	<span>7</span>月<span>21</span>日乐山考察。感谢乐山市委常委、宣传部长赖淑芳，乐山市委宣传部副部长、乐山市文联主席胡玉玲，乐山市文化馆馆长陈一华等拨冗考察或交流，乐山的文化艺术事业发展一路向上。\r\n</p>\r\n<p style=\"margin-left:0cm;text-indent:48.0pt;\">\r\n	<img src=\"/Public/image/20160826/20160826103058_77596.jpg\" alt=\"\" /><br />\r\n<span></span>\r\n</p>', null, null, '2', '7月21日乐山考察。感谢乐山市委常委、宣传部长赖淑芳，乐山市委宣传部副部长、乐山市文联主席胡玉玲，乐山市文化馆馆长陈一华等拨冗考察或交流，乐山的文化艺术事业发展一路向上。', '1430755200', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('36', '2016宝墩春季拍卖会拍卖公告', '<p>\r\n	<a name=\"OLE_LINK16\"></a><b>一、拍卖全称<span></span></b> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span> <span>2016</span>宝墩春季拍卖《巴蜀名家<span>“</span>一平尺<span>”</span>作品拍卖会》<span></span></span> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span><b>二、预展时间<span></span></b></span> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span> <span>2016</span>年<span>5</span>月<span>6</span>日<span>—5</span>月<span>8</span>日，每天上午<span>9:00—</span>下午<span>17:00</span></span> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span><b>三、预展地点<span></span></b></span> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span> 成都市世纪城新会展中心<span>7</span>号馆<span></span></span> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span><b>四、拍卖时间<span></span></b></span> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span> <span>2016</span>年<span>5</span>月<span>8</span>日下午<span>14:00</span>举槌<span></span></span> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span><b>五、拍卖地点<span></span></b></span> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span> 成都市世纪城新会展中心假日酒店锦江厅<span></span></span> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span><b>六、联系方式<span></span></b></span> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span> 雅昌在线预展网址：<span>http://www.artron.net/</span></span> \r\n</p>\r\n<p>\r\n	<span> 宝墩拍卖官方网站：<span>www.baodunwang.com</span></span> \r\n</p>\r\n<p>\r\n	<span> 中华美网预展网址：<span>http://www.zhonghuameiwang.com/</span></span> \r\n</p>\r\n<p>\r\n	<span> 联系电话：<span>028-89897657</span>、<span>028-89897650</span></span> \r\n</p>\r\n<p>\r\n	<span> 联系地址：成都市锦江区锦华路三段<span>88</span>号<span>A</span>座<span>10</span>楼<span></span></span> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span><b>七、拍卖承诺<span></span></b></span> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span> 四川宝墩拍卖有限公司是巴蜀画派集团的全资子公司，承担巴蜀画派各位艺术家的作品拍卖工作。宝墩拍卖承诺，将用完全保真的作品服务各位藏家，坚持<span>“</span>求真、求实<span>”</span>的原则，致力营造健康、诚信、可持续发展的艺术品交易市场。<span></span></span> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span><b>八、拍卖会简介<span></span></b></span> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span> <span>2016</span>宝墩春季拍卖会主题为《巴蜀名家<span>“</span>一平尺<span>”</span>作品拍卖会》。主打极具市场优势的小幅面作品，作品价格适中，实用价值与收藏价值兼备，旨在让艺术家\r\n的一笔一画真正走进生活，走进寻常百姓家。拍品来自巴蜀画派知名艺术家，作品的艺术性和市场性均经过四川宝墩拍卖有限公司严格把关，力求为藏家挑选出的作 品：题材广泛、特色鲜明，饱含深厚的艺术价值，极具市场升值空间和较高的收藏价值。<span></span></span> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span> <a name=\"OLE_LINK18\"></a><a name=\"OLE_LINK17\"></a><span>拍卖会作品预展将于<span>5</span>月<span>6</span>日 至<span>8</span>日在世纪城新会展中心<span>7</span>号馆与<span>2016</span>第二届巴蜀国际艺博会同期举行。拍卖汇集了<span>30</span>余位巴蜀画派名家：周抡园、万一宾、刘朴、朱常棣、任光荣、周明 安、米金铭、吴浩、陈志才、贾兴桐、简崇民、马光剑、何冠霖、东方腾弘、崔治中等知名艺术家的力作，共计<span>70</span>余幅，包含国画、油画、书法作品。<span></span></span></span> \r\n</p>\r\n<span></span><span></span> \r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p>\r\n	<span> 届时，<span>2016</span>宝墩春季拍卖会将与国内外艺术机构携手带来五月初的艺术盛宴，为藏家和收藏爱好者带来超值的书画艺术收藏精品。<span></span></span> \r\n</p>\r\n<p>\r\n	<span><span> </span></span> \r\n</p>\r\n<p style=\"text-align:right;\" align=\"right\">\r\n	<span>四川宝墩拍卖有限公司<span></span></span> \r\n</p>\r\n<p style=\"text-align:right;\" align=\"right\">\r\n	<span><span>2016</span>年<span>4</span>月<span>26</span>日<span></span></span> \r\n</p>', null, null, '2', '<p>\r\n	<span><span>2016</span>宝墩春季拍卖会主题为《巴蜀名家<span>“</span>一平尺<span>”</span>作品拍卖会》。主打极具市场优势的小幅面作品，作品价格适中，实用价值与收藏价值兼备，旨在让艺术家\r\n的一笔一画真正走进生活，走进寻常百姓家。拍品来自巴蜀画派知名艺术家，作品的艺术性和市场性均经过四川宝墩拍卖有限公司严格把关，力求为藏家挑选出的作 品：题材广泛、特色鲜明，饱含深厚的艺术价值，极具市场升值空间和较高的收藏价值。</span>\r\n</p>\r\n<p>\r\n	<span><span><span>拍卖会作品预展将于<span>5</span>月<span>6</span>日 至<span>8</span>日在世纪城新会展中心<span>7</span>号馆与<span>2016</span>第二届巴蜀国际艺博会同期举行。拍卖汇集了<span>30</span>余位巴蜀画派名家：周抡园、万一宾、刘朴、朱常棣、任光荣、周明 安、米金铭、吴浩、陈志才、贾兴桐、简崇民、马光剑、何冠霖、东方腾弘、崔治中等知名艺术家的力作，共计<span>70</span>余幅，包含国画、油画、书法作品。</span></span><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '1430668800', '1', null, '1', '1');
INSERT INTO `sp_news1111` VALUES ('37', '首提“巴蜀双艺博”新概念 下月开幕惊艳蓉城', '<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\" align=\"center\">\r\n	<img src=\"/Public/image/20160826/20160826104409_57169.png\" alt=\"\" />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;line-height:24px;\">2016年5月6日至9日，<span>2016第二届巴蜀国际艺术博览会将与2016首届巴蜀青少年艺博会<span>在成都市世纪城新国际会展中心</span>同期举办。</span></span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;line-height:24px;\">组委会首次提出“双艺博”概念，<span>大胆开辟一条艺术展会的“另类”之路。</span></span><span style=\"font-size:14px;line-height:1.6;\">以<span style=\"line-height:22px;\">总面积达2.4万平方米的超大展馆空间，</span>保证两个艺博会独立运行的同时，也通过</span><span style=\"font-size:14px;\">“无缝艺术展墙”</span><span style=\"font-size:14px;line-height:24px;\">使巴蜀国际艺博会的<span style=\"line-height:22px;\">“专业化、民族化、国际化”与巴蜀青少年艺博会<span style=\"line-height:24px;\">“展示、创想、乐教、互动”的主元</span></span></span><span style=\"font-size:14px;\">素相碰撞。此外，组<span style=\"line-height:22px;\">委会特意从北京定制了更适合美术作品展陈的展板，也邀请到专业的布展团队，<span>将以更加专业、科学、合理、精美的</span>展陈效果呈现在观众面前。</span></span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\" align=\"center\">\r\n	<span style=\"font-size:14px;\"><span style=\"line-height:22px;\"><img src=\"/Public/image/20160826/20160826104447_91977.png\" alt=\"\" /><br />\r\n</span></span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\" align=\"center\">\r\n	<span style=\"font-size:14px;\"><span style=\"line-height:22px;\"> </span></span> \r\n</p>\r\n<p style=\"text-align:left;text-indent:2em;\">\r\n	<span style=\"font-size:14px;color:#000000;\">2016第二届巴蜀国际艺术博览会以“艺术巴蜀”为核心主题，以“艺术金融”为办展宗旨，为国内外艺术家和收藏家提供近距离交流的机会，促进金融机构与艺术品市场的深度对接，为艺术产业的发展提供强大的艺术金融融合发展机遇，为艺术品投资理财提供优质服务。&nbsp;同时，着力展示有高水准艺术价值的艺术作品，积极表现国际艺术的精髓以及国内艺术家的深厚积淀与创新发展。<br />\r\n</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;text-indent:2em;\">\r\n	<span style=\"font-size:14px;color:#000000;\">本届艺博会由中国国际文化交流中心、中共四川省委宣传部、四川省文学艺术界联合会指导，中国画院、四川省诗书画院支持，中华美网、四川国际文化交流中心、四川省美术家协会、四川省书法家协会、四川省巴蜀画派促进会、成都国家广告产业园联合主办，四川巴蜀画派文化产业集团承办，四川文化产业职业学院、四川新闻网传媒集团股份有限公司、四川宝墩拍卖有限公司、四川宝墩艺术品有限公司、四川巴蜀画派传媒有限公司、四川巴蜀画派专修学院协办。</span> \r\n</p>\r\n<p style=\"text-align:left;text-indent:2em;\">\r\n	<span style=\"font-size:14px;color:#000000;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:left;text-indent:0em;\">\r\n	<span style=\"font-size:14px;color:#000000;\"></span> \r\n</p>\r\n<span class=\"135brush\" style=\"color:#FFFFFF;font-family:inherit;text-decoration:inherit;background-color:#374AAE;\">四大主题展区</span> \r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;text-indent:2em;\">\r\n	<span style=\"color:#007AAA;\"><strong><span style=\"font-size:14px;\">艺术品展区</span></strong><strong><span style=\"font-size:14px;\">：</span></strong></span><span style=\"text-decoration:inherit;font-size:14px;color:#000000;\">国画、油画、版画、雕塑、书法、篆刻、素描、</span><span style=\"text-decoration:inherit;color:#000000;font-size:14px;line-height:1.6;\">水彩、水粉、影像艺术、装置艺术；</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;text-indent:2em;\">\r\n	<span style=\"color:#007AAA;\"><strong><span style=\"font-size:14px;\">艺术材料</span></strong><strong><span style=\"font-size:14px;\">展区</span></strong><span style=\"font-size:14px;\">：</span></span><span style=\"color:#000000;font-size:14px;text-decoration:inherit;line-height:1.6;\">文房四宝、装裱工程工艺、艺术耗材等；</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;text-indent:2em;\">\r\n	<span style=\"color:#007AAA;\"><strong><span style=\"font-size:14px;\">艺术传媒展区</span></strong><strong><span style=\"font-size:14px;\">：</span></strong></span><span style=\"color:#000000;font-size:14px;text-decoration:inherit;line-height:1.6;\">艺术报刊、艺术出版物、艺术新媒体；</span> \r\n</p>\r\n<p style=\"text-align:left;text-indent:2em;\">\r\n	<span style=\"font-size:14px;color:#000000;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:left;text-indent:2em;\">\r\n	<span style=\"color:#007AAA;\"><strong><span style=\"font-size:14px;\">藏品展区：</span></strong></span><span style=\"font-size:14px;color:#000000;\">瓷器、玉器、雕品、漆器、珠宝、翡翠、紫砂、</span><span style=\"color:#000000;font-size:14px;text-decoration:inherit;line-height:1.6;\">织绣艺术等。</span> \r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\" align=\"center\">\r\n	<span style=\"font-size:14px;\"><span style=\"line-height:22px;\"><br />\r\n</span></span> \r\n</p>\r\n<div align=\"center\">\r\n	<p>\r\n		<span style=\"font-size:14px;line-height:24px;\"><img src=\"/Public/image/20160826/20160826104559_70831.png\" alt=\"\" /><br />\r\n</span>\r\n	</p>\r\n	<p>\r\n		<p style=\"text-align:left;text-indent:2em;\">\r\n			<span style=\"font-size:14px;color:#000000;\"><span style=\"line-height:22px;\">2016首届巴蜀青少年艺术博览会是为青少年儿童艺术教育而服务的博览盛会，以“孩子•成长”、“创意•想象”为核心理念，充分展示成都市整体的艺术教育经验和成果。参加本届博览会活动的团体主要是公立的中小学教育机构、民营的社会艺术培训机构。博览会旨在为成都市中小学校提供一个展示艺术教育特色的平台，为民营艺术教育机构搭建一个与大众交流的窗口，为青少年儿童提供一个全方位艺术学习体验平台。通过孩子带动家庭，通过学校带动社会，促进成都市青少年儿童艺术教育事业长足发展。</span></span>\r\n		</p>\r\n		<p>\r\n			<br />\r\n		</p>\r\n		<p style=\"text-align:left;text-indent:2em;\">\r\n			<span style=\"font-size:14px;color:#000000;\">本届艺博会由成都市博览局、成都市教育局支持举办，四川省美协、四川省书协、四川省巴蜀画派促进会三家权威机构主办，四川巴蜀画派青少年艺术交流中心、巴蜀画派专修学院承办，成都市教育学会、成都市民办教育协会、成都市美协、成都市书协协办。</span>\r\n		</p>\r\n		<p style=\"text-align:left;text-indent:2em;\">\r\n			<span style=\"font-size:14px;color:#000000;\"><br />\r\n</span>\r\n		</p>\r\n		<h2 style=\"font-size:27px;\">\r\n			<strong><span style=\"font-size:14px;\"></span></strong>\r\n		</h2>\r\n<span class=\"135brush\" style=\"color:#FFFFFF;font-family:inherit;text-decoration:inherit;background-color:#EF7060;\">六大特色展区</span>\r\n		<h2 style=\"font-size:27px;\">\r\n			<strong><span style=\"font-size:14px;\"></span></strong><br />\r\n		</h2>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"color:#FFA900;\"><strong><span style=\"font-size:14px;\">艺术教育机构成果展区：</span></strong></span>\r\n		</h2>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"font-size:14px;color:#000000;\"><br />\r\n</span>\r\n		</h2>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"font-size:14px;color:#000000;\">汇集部分成都市中小学（公办）、主流民营艺术教育培训机构，充分展示学校及培训机构的艺术教育成果及经验分享，实现与家长、学生的现场互动交流。</span>\r\n		</h2>\r\n		<p>\r\n			<br />\r\n		</p>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"color:#FFA900;\"><strong><span style=\"font-size:14px;\">艺术教育论坛专区（首届巴蜀艺术教育论坛）：</span></strong></span>\r\n		</h2>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"color:#000000;font-size:14px;line-height:1.6;text-decoration:inherit;\"><br />\r\n</span>\r\n		</h2>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"color:#000000;font-size:14px;line-height:1.6;text-decoration:inherit;\">本届艺博会将举办“首届巴蜀艺术教育论坛”，将邀请艺术教育领域专家就“青少年儿童艺术教育现状与发展趋势”的前沿问题进行深入探讨，围绕中小学艺术教育、特殊儿童艺术教育、艺术教育在孩子成长中的作用等多方面展开专题论坛。</span>\r\n		</h2>\r\n		<p>\r\n			<br />\r\n		</p>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"color:#FFA900;\"><strong><span style=\"font-size:14px;\">书画作品专区：</span></strong></span>\r\n		</h2>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"color:#000000;font-size:14px;line-height:1.6;text-decoration:inherit;\"><br />\r\n</span>\r\n		</h2>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"color:#000000;font-size:14px;line-height:1.6;text-decoration:inherit;\">征集成都市各大中小学校原创优秀书画作品，汇集成都市各类青少年儿童创意大赛优秀作品进行展示展览。同时，在本区现场，孩子和家长可以进行DIY创作、涂鸦等艺术活动，增加孩子们的动手能力，让每一个孩子在现场都能体验到绘画的乐趣。</span>\r\n		</h2>\r\n		<p>\r\n			<br />\r\n		</p>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"color:#FFA900;\"><strong><span style=\"font-size:14px;\">童星耀巴蜀青少年才艺汇演区：</span></strong></span>\r\n		</h2>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"color:#000000;font-size:14px;line-height:1.6;text-decoration:inherit;\"><br />\r\n</span>\r\n		</h2>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"color:#000000;font-size:14px;line-height:1.6;text-decoration:inherit;\">专门为舞蹈、音乐、表演等知名艺术培训机构开设的舞台展演区。届时将为现场观众带来精彩纷呈的节目表演。</span>\r\n		</h2>\r\n		<p>\r\n			<br />\r\n		</p>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"color:#FFA900;\"><strong><span style=\"font-size:14px;\">音乐器材展区：</span></strong></span>\r\n		</h2>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"color:#000000;font-size:14px;line-height:1.6;text-decoration:inherit;\"><br />\r\n</span>\r\n		</h2>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"color:#000000;font-size:14px;line-height:1.6;text-decoration:inherit;\">展出中西乐器、键盘乐器、打击乐器、乐器零部件等，将让观众了解到种类最全、质量最好的乐器。</span>\r\n		</h2>\r\n		<p>\r\n			<br />\r\n		</p>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"color:#FFA900;\"><strong><span style=\"font-size:14px;\">特色互动区：</span></strong><strong><span style=\"font-size:14px;\"></span></strong></span>\r\n		</h2>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"color:#000000;font-size:14px;line-height:1.6;text-decoration:inherit;\"><br />\r\n</span>\r\n		</h2>\r\n		<h2 style=\"font-weight:400;font-size:16px;text-align:left;text-indent:2em;\">\r\n			<span style=\"color:#000000;font-size:14px;line-height:1.6;text-decoration:inherit;\">本展区设置了游戏互动和动漫表演环节。学生和家长们既可以参加轮滑、亲子活动等竞技项目，也可以观看到cosplay表演，并有机会加入到表演中，进入特设换装区，亲自体验。</span>\r\n		</h2>\r\n<img src=\"/Public/image/20160826/20160826104819_53893.png\" alt=\"\" /><br />\r\n<span style=\"font-size:14px;line-height:24px;\"></span>\r\n	</p>\r\n</div>', null, null, '1', '2016年5月6日至9日，2016第二届巴蜀国际艺术博览会将与2016首届巴蜀青少年艺博会在成都市世纪城新国际会展中心同期举办。\r\n\r\n\r\n组委会首次提出“双艺博”概念，大胆开辟一条艺术展会的“另类”之路。以总面积达2.4万平方米的超大展馆空间，保证两个艺博会独立运行的同时，也通过“无缝艺术展墙”使巴蜀国际艺博会的“专业化、民族化、国际化”与巴蜀青少年艺博会“展示、创想、乐教、互动”的主元素相碰撞。此外，组委会特意从北京定制了更适合美术作品展陈的展板，也邀请到专业的布展团队，将以更加专业、科学、合理、精美的展陈效果呈现在观众面前。', '1430582400', '1', null, '0', '1');
INSERT INTO `sp_news1111` VALUES ('38', '”乐教·创新“添彩首届巴蜀青少年艺博会', '<div align=\"center\">\r\n	<p>\r\n		<img src=\"/Public/image/20160826/20160826105237_88950.png\" alt=\"\" />\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;line-height:28.4444px;\">&nbsp;2016年5月6日到9日，首届巴蜀青少年艺博会将在成都世纪城新国际会展中心举办。本届艺博会由成都市博览局、成都市教育局支持举办，四川省美协、四川省书协、四川省巴蜀画派促进会三家权威机构主办，四川巴蜀画派青少年艺术交流中心、巴蜀画派专修学院承办，成都市教育学会、成都市民办教育协会、成都市美协、成都市书协协办。</span>\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;\"> 2016首届巴蜀青少年艺术博览会是以“成长与创想”为办展方针，以展示、创想、乐教、互动为主要形式，为青少年艺术教育提供全面服务的博览盛会，展出面积达8000平方米，包括“成都艺术特色学校展区、艺术教育机构展区、书画作品征集展区、才艺汇演互动区”四个区域。参展机构主要是公立的中小学教育机构、民营的社会艺术培训机构。博览会旨在为巴蜀地区中小学校提供一个展示艺术特色教育的平台，为民营艺术教育机构搭建一个与大众交流的窗口，为青少年提供一个全方位艺术学习体验的机会，充分展示艺术教育经验和成果。通过孩子带动家庭，通过学校带动社会，共同促进青少年艺术教育事业大发展。</span>\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n<img src=\"/Public/image/20160826/20160826105326_53104.png\" alt=\"\" />\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<img src=\"/Public/image/20160826/20160826105353_20148.png\" alt=\"\" />\r\n	</p>\r\n	<p>\r\n		<span style=\"color:#3E3E3E;font-size:14px;line-height:24.8889px;background-color:#FFFFFF;\">&nbsp;</span><span style=\"font-size:14px;\">截止目前，已邀请到成都演艺集团、成演艺博、川音大地、成都青少年宫、四川双流艺体中学等带有艺术特色的中小学校和艺术机构前来参展。届时将为广大观众献上一个不一样的精彩会展。</span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;\"><br />\r\n</span>\r\n	</p>\r\n	<p>\r\n		<strong><span style=\"font-size:16px;color:#FFFFFF;background-color:#3DA742;\">参展机构提前一览</span></strong>\r\n	</p>\r\n	<p align=\"left\">\r\n		<strong><span style=\"font-size:16px;color:#FFFFFF;background-color:#3DA742;\"><span class=\"135brush\" style=\"background-color:#7F9DB9;\">成都演艺集团 <br />\r\n</span></span></strong>\r\n	</p>\r\n	<p align=\"center\">\r\n		<strong><span style=\"font-size:16px;color:#FFFFFF;background-color:#3DA742;\"><span class=\"135brush\" style=\"background-color:#7F9DB9;\"><img src=\"/Public/image/20160826/20160826105601_37762.png\" alt=\"\" /><br />\r\n</span></span></strong>\r\n	</p>\r\n	<p align=\"left\">\r\n		<strong><span style=\"font-size:16px;color:#FFFFFF;background-color:#3DA742;\"><span class=\"135brush\" style=\"background-color:#7F9DB9;\"><span style=\"font-size:14px;\">成都演艺集团是成都乃至整个中国西部最大的演艺集团，是中国文化演出界的领航者之一。其历经12年的风雨历程，成演集团在政府、企业界及大众心目中已成为蓉城的一张文化名片，拥有巨大的品牌号召力与影响力。<span class=\"135brush\" style=\"background-color:#7F9DB9;\"><br />\r\n</span></span></span></span></strong>\r\n	</p>\r\n	<p align=\"left\">\r\n		<strong><span style=\"font-size:16px;color:#FFFFFF;background-color:#3DA742;\"><span class=\"135brush\" style=\"background-color:#7F9DB9;\"><span style=\"font-size:14px;\"><span class=\"135brush\" style=\"background-color:#7F9DB9;\"></span><span class=\"135brush\" style=\"background-color:#7F9DB9;\"></span><br />\r\n</span></span></span></strong>\r\n	</p>\r\n	<p align=\"left\">\r\n		<strong><span style=\"font-size:16px;color:#FFFFFF;background-color:#3DA742;\"><span class=\"135brush\" style=\"background-color:#7F9DB9;\"><span style=\"font-size:14px;\"><span class=\"135brush\" style=\"background-color:#7F9DB9;\"></span></span></span></span></strong>\r\n	</p>\r\n	<p align=\"left\">\r\n		<strong><strong><span style=\"font-size:16px;color:#FFFFFF;background-color:#3DA742;\"><span class=\"135brush\" style=\"background-color:#7F9DB9;\">成都青少年宫<span class=\"135brush\" style=\"background-color:#7F9DB9;\"></span></span></span></strong></strong>\r\n	</p>\r\n<strong><span style=\"font-size:16px;color:#FFFFFF;background-color:#3DA742;\"><span class=\"135brush\" style=\"background-color:#7F9DB9;\">\r\n	<p>\r\n		<strong><span style=\"font-size:16px;color:#FFFFFF;background-color:#3DA742;\"><span class=\"135brush\" style=\"background-color:#7F9DB9;\"><img src=\"/Public/image/20160826/20160826105925_60638.png\" alt=\"\" /><img src=\"/Public/image/20160826/20160826105936_77747.png\" alt=\"\" /></span></span></strong>\r\n	</p>\r\n</span></span></strong>\r\n	<p>\r\n		<span style=\"font-size:14px;\"> 成都市青少年宫始建于一九五八年，当时名称为“成都市少年之家”，一九八三年三月正式更名为青少年宫，是具有五十多年历史的青少年校外教育、活动阵地。</span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;在\r\n成都市历届市委、市政府的关怀下，成都市青少年宫已建设形成“一宫五区”、“三校五部”的工作格局。全宫占地面积约230亩，建筑使用面积约4.5万平方\r\n米。拥有200余名职工，开展艺术、体育、科技、文化、社会实践、营地教育等60多个项目的能力培训、兴趣活动和公益，年培训人数5万余人，参加活动人员\r\n30余万人次，发挥了成都市青少年素质教育基地、全国青少年校外活动示范基地重要作用，为全市青少年思想道德建设贡献了力量。</span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;\"><br />\r\n</span>\r\n	</p>\r\n	<p align=\"left\">\r\n		<span style=\"font-size:14px;\"><strong><strong><span style=\"font-size:16px;color:#FFFFFF;background-color:#3DA742;\"><span class=\"135brush\" style=\"background-color:#7F9DB9;\"><span class=\"135brush\" style=\"background-color:#7F9DB9;\">四川双流艺体中学</span></span></span></strong></strong></span>\r\n	</p>\r\n	<p align=\"left\">\r\n		<span style=\"font-size:14px;\"><strong><strong><span style=\"font-size:16px;color:#FFFFFF;background-color:#3DA742;\"><span class=\"135brush\" style=\"background-color:#7F9DB9;\"><span class=\"135brush\" style=\"background-color:#7F9DB9;\"></span></span></span></strong></strong><img src=\"/Public/image/20160826/20160826110239_34288.png\" alt=\"\" /><br />\r\n</span>\r\n	</p>\r\n	<p align=\"left\">\r\n		<span style=\"font-size:14px;\"><img src=\"/Public/image/20160826/20160826110256_54582.png\" alt=\"\" /><br />\r\n</span>\r\n	</p>\r\n	<p align=\"left\">\r\n		<span style=\"font-size:14px;\"><span style=\"font-size:14px;\">四川省双流艺体中学，前身双流县彭镇中学，始建于1958年，1996年县政决定并报成都市教委批准改革，走上“普通高中+艺术体育”办学模式、“合格+特长”育人目标之路。2002年建成“成都市示范性普通高中”，2011年获“四川省特色示范学校”称号。2005年以来先后荣获“民主法制示范校”、“学习型学校”、“中国西部名校”、“全国中小学科研兴校示范基地”、全国“首届百佳特色中学”等称号。</span><br />\r\n</span>\r\n	</p>\r\n	<p align=\"left\">\r\n		<span style=\"font-size:14px;\"><img src=\"/Public/image/20160826/20160826110326_77151.png\" alt=\"\" /><br />\r\n</span>\r\n	</p>\r\n<br />\r\n</div>', null, null, '1', ' 2016年5月6日到9日，首届巴蜀青少年艺博会将在成都世纪城新国际会展中心举办。本届艺博会由成都市博览局、成都市教育局支持举办，四川省美协、四川省书协、四川省巴蜀画派促进会三家权威机构主办，四川巴蜀画派青少年艺术交流中心、巴蜀画派专修学院承办，成都市教育学会、成都市民办教育协会、成都市美协、成都市书协协办。\r\n\r\n\r\n　　2016首届巴蜀青少年艺术博览会是以“成长与创想”为办展方针，以展示、创想、乐教、互动为主要形式，为青少年艺术教育提供全面服务的博览盛会，展出面积达8000平方米，包括“成都艺术特色学校展区、艺术教育机构展区、书画作品征集展区、才艺汇演互动区”四个区域。参展机构主要是公立的中小学教育机构、民营的社会艺术培训机构。博览会旨在为巴蜀地区中小学校提供一个展示艺术特色教育的平台，为民营艺术教育机构搭建一个与大众交流的窗口，为青少年提供一个全方位艺术学习体验的机会，充分展示艺术教育经验和成果。通过孩子带动家庭，通过学校带动社会，共同促进青少年艺术教育事业大发展。\r\n\r\n\r\n', '1430496000', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('39', '“二酉山房”增色2016巴蜀国际艺博会', '<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826110815_22389.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	2016第二届巴蜀国际艺术博览会经过几个月的精心筹备，现已进入倒计时阶段，将于2016年5月6日至9日在成都市世纪城新国际会展中心隆重举行。届时，海内外优秀的艺术家、艺术机构、艺术教育机构、艺术产业园、画廊、艺术新媒介、非物质文化遗产等都将展现各自的风采。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	本届艺博会由中国国际文化交流中心、四川省文学艺术界联合会指导，中国画院、四川省诗书画院支持，中华美网、四川国际文化交流中心、四川省美术家协会、\r\n四川省书法家协会、四川省巴蜀画派促进会、成都国家广告产业园联合主办，四川巴蜀画派文化产业集团承办，四川文化产业职业学院、四川新闻网传媒集团股份有\r\n限公司、四川宝墩拍卖有限公司、四川宝墩艺术品有限公司、四川巴蜀画派传媒有限公司、四川巴蜀画派专修学院协办。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826110816_34554.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	2016巴蜀国际艺术博览会得到社会各界越来越多的重视和关注，不仅艺术家热情加入，艺术品牌和艺术机构也相继加盟。编者今天从巴蜀国际艺博会组委会获悉，我国著名画家郭汝愚先生曾祖父郭述俊于清代道光创建的“二酉山房”也将加盟，携众多高品位艺术品参展。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	“二\r\n酉山房”专营古籍善本及木版印刷。二酉是湖南沅陵县大酉山和小酉山的简称，秦始皇焚书坑儒，不少读书人逃难于此，在一山穴中有藏书千卷，后为遂称藏书多为\r\n“二酉”。辛亥革命前夕，郭文泰去世，由族人郭纪元继承，他把业务拓展到周边各县，在上世纪二三十年代成为四川四大古籍书馆之首（成都二酉山房、正古堂、\r\n博文堂，重庆存耕堂）。郭纪元去世之后由郭汝愚父亲郭士炯继任。新中国成立后实行公私合营，收为国有。如今郭汝愚先生作为二酉山房的品牌继承人，为了更好\r\n的继承和发扬祖上的历史品牌，宣传和推广成都本土地区的历史品牌文化，重新恢复经营二酉山房，并于2014年兼并成都净砚斋画廊。目前主要从事艺术家的发\r\n掘与推广、展览、艺术培训、艺术品销售等业务。\r\n</p>\r\n<p>\r\n	<strong>参展作品欣赏：</strong>\r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826110816_93465.png\" alt=\"\" />\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	曹辉 &nbsp; 湖边 &nbsp; 70×110cm &nbsp; 7平方尺&nbsp;&nbsp;&nbsp;\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826110816_78406.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	陈志才 &nbsp; &nbsp; 消夏图 &nbsp; 69×34cm\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826110816_38638.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	郭汝愚 &nbsp; &nbsp; &nbsp; 月皎惊鸟栖不定 &nbsp; &nbsp; 70×24cm<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826110816_11296.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	郭汝愚 &nbsp; 春山雾重 &nbsp; &nbsp; 135×29cm\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826110816_61462.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	陶渊明 归去来兮57×61cm\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826110816_60685.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	吴浩 &nbsp; &nbsp; 超能大白 &nbsp; &nbsp;30×14.5cm\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826110816_25180.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	张承翥 &nbsp; 桃源行-173x186cm-2013\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826110816_79971.png\" alt=\"\" />\r\n</p>', null, null, '2', '2016第二届巴蜀国际艺术博览会经过几个月的精心筹备，现已进入倒计时阶段，将于2016年5月6日至9日在成都市世纪城新国际会展中心隆重举行。届时，海内外优秀的艺术家、艺术机构、艺术教育机构、艺术产业园、画廊、艺术新媒介、非物质文化遗产等都将展现各自的风采。\r\n\r\n\r\n　　本届艺博会由中国国际文化交流中心、四川省文学艺术界联合会指导，中国画院、四川省诗书画院支持，中华美网、四川国际文化交流中心、四川省美术家协会、四川省书法家协会、四川省巴蜀画派促进会、成都国家广告产业园联合主办，四川巴蜀画派文化产业集团承办，四川文化产业职业学院、四川新闻网传媒集团股份有限公司、四川宝墩拍卖有限公司、四川宝墩艺术品有限公司、四川巴蜀画派传媒有限公司、四川巴蜀画派专修学院协办。\r\n\r\n2016巴蜀国际艺术博览会得到社会各界越来越多的重视和关注，不仅艺术家热情加入，艺术品牌和艺术机构也相继加盟。编者今天从巴蜀国际艺博会组委会获悉，我国著名画家郭汝愚先生曾祖父郭述俊于清代道光创建的“二酉山房”也将加盟，携众多高品位艺术品参展。', '1430409600', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('40', '2016第二届巴蜀国际艺博会前瞻：迥异视角展现川西高原风光', '<p>\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826113043_61416.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\r\n		<span style=\"font-size:14px;\">距离2016第二届巴蜀国际艺术博览会开幕仅余十几天，经过组委会几个月的精心筹备，现已接收到并筛选出，来自<span style=\"line-height:22px;\">海内外优秀的艺术家、艺术机构、艺术产业园、画廊、艺术新媒介、非物质文化遗产<span>的大量优质参展作品。</span></span></span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\r\n		<span style=\"font-size:14px;\"><span style=\"line-height:22px;\"><br />\r\n</span></span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\r\n		<span style=\"font-size:14px;\">其中，来自阿坝州的两位参展艺术家：刘晓平和大唐卓玛，带来了描绘川西高原自然风光的油画作品。两位艺术家迥然不同的艺术风格将带给我们独特而多样的艺术视角。而在这迥异的视角之下，是一片雄奇俊美的天地，这里<span style=\"line-height:22px;\">草原辽阔、雪山高耸、牧歌悠扬，而<span>蓝天下盛开喜悦的心灵之花将向你细细讲述<span>高原孕育的古老、神秘的文明。</span></span></span></span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\r\n		<span style=\"font-size:14px;line-height:1.6;\"><br />\r\n</span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\r\n		<span style=\"font-size:14px;line-height:1.6;\">本届艺博会由中国国际文化交流中心、中共四川省委宣传部、四川省文学艺术界联合会指导，中国画院、四川省诗书画院支持，中华美网、四川国际文化交流中心、四川省美术家协会、四川省书法家协会、四川省巴蜀画派促进会、成都国家广告产业园联合主办，四川巴蜀画派文化产业集团承办，四川文化产业职业学院、四川新闻网传媒集团股份有限公司、四川宝墩拍卖有限公司、四川宝墩艺术品有限公司、四川巴蜀画派传媒有限公司、四川巴蜀画派专修学院协办。展会将于2</span><span style=\"font-size:14px;line-height:22px;\">016年5月6日至9日在成都市</span><span style=\"font-size:14px;line-height:22px;\">世纪城新国际会展中心隆重举行。</span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n		<span style=\"font-size:14px;line-height:1.6;\"></span>\r\n	</p>\r\n川西高原 风光一览\r\n</p>\r\n<p>\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826113043_23252.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<p style=\"text-indent:2em;\">\r\n		<span style=\"font-size:14px;\">刘晓平，</span><span style=\"font-size:14px;line-height:1.6;\">四川省美术家协会会员，阿坝州美协油画艺委会主任，阿坝师范学院美术系副教授。</span>\r\n	</p>\r\n</p>\r\n<p>\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826113043_69496.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	▲&nbsp;刘晓平《阿尔》160x90cm\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826113043_74333.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	▲&nbsp;刘晓平《安乡·雪》40x50cm\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826113043_71380.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	▲&nbsp;刘晓平《布兰》60x80cm\r\n</p>\r\n<p>\r\n</p>\r\n<p>\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826113043_38229.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	▲&nbsp;刘晓平《铁邑寨之晨》160x200cm\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826113043_12518.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	大唐卓玛 (唐&nbsp; 平) ，四川省美术家协会会员，阿坝州美协秘书长，阿坝师范学院副教授。\r\n</p>\r\n<p>\r\n</p>\r\n<p>\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826113043_30154.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	▲&nbsp;大唐卓玛《藏风景——神座》之七 65x73cm 2014年\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826113043_44175.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	▲&nbsp;大唐卓玛《藏风景——麦尔玛》之二 70x90cm 2014年\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826113043_29841.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	▲&nbsp;大唐卓玛《藏风景——麦哇》之一 40x80cm 2015年\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826113044_57859.png\" alt=\"\" />\r\n</p>', null, null, '2', '距离2016第二届巴蜀国际艺术博览会开幕仅余十几天，经过组委会几个月的精心筹备，现已接收到并筛选出，来自海内外优秀的艺术家、艺术机构、艺术产业园、画廊、艺术新媒介、非物质文化遗产的大量优质参展作品。\r\n其中，来自阿坝州的两位参展艺术家：刘晓平和大唐卓玛，带来了描绘川西高原自然风光的油画作品。两位艺术家迥然不同的艺术风格将带给我们独特而多样的艺术视角。而在这迥异的视角之下，是一片雄奇俊美的天地，这里草原辽阔、雪山高耸、牧歌悠扬，而蓝天下盛开喜悦的心灵之花将向你细细讲述高原孕育的古老、神秘的文明\r\n本届艺博会由中国国际文化交流中心、中共四川省委宣传部、四川省文学艺术界联合会指导，中国画院、四川省诗书画院支持，中华美网、四川国际文化交流中心、四川省美术家协会、四川省书法家协会、四川省巴蜀画派促进会、成都国家广告产业园联合主办，四川巴蜀画派文化产业集团承办，四川文化产业职业学院、四川新闻网传媒集团股份有限公司、四川宝墩拍卖有限公司、四川宝墩艺术品有限公司、四川巴蜀画派传媒有限公司、四川巴蜀画派专修学院协办。展会将于2016年5月6日至9日在成都市世纪城新国际会展中心隆重举行。\r\n\r\n', '1429545600', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('41', '广元市美协入驻2016巴蜀艺博会', '<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826114245_96226.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<p style=\"text-align:center;color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n		<span style=\"color:#000000;\"><span style=\"font-size:13.6364px;line-height:20px;\">距离2016第二届巴蜀国际艺术博览会开幕仅余十几天，经过组委会几个月的精心筹备，现已接收到并筛选出来自</span><span style=\"font-size:14px;line-height:22px;\">海内外优秀的艺术家、艺术机构、艺术产业园、画廊、艺术新媒介、非物质文化遗产<span>的大量优质参展作品。</span></span></span>\r\n	</p>\r\n	<p style=\"text-align:center;color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n		<span style=\"color:#000000;font-size:14px;line-height:22px;\"><br />\r\n</span>\r\n	</p>\r\n	<p style=\"text-align:center;color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;line-height:22px;\"> &nbsp; &nbsp; &nbsp; &nbsp; 其中，广元市美术家协会将展出11位优秀艺术家的作品。</span></span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n		<span style=\"font-size:14px;color:#000000;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-size:13.6364px;color:#000000;line-height:23.9915px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"font-size:14px;line-height:23.9915px;\">剑门关地处四川省广元市剑阁县，因唐代大诗人李白《蜀道难》中“剑阁峥嵘而崔嵬，一夫当关，万夫莫开”的赞誉名扬海内外，它集蜀道文化、三国文化、战争文化、红色文化于一体，有“天下第一雄关”之称。剑门山东南延伸绵延数百里，这里群峰突兀，山涛云海，成为艺术家笔下最爱勾勒的对象。</span></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-size:14px;color:#000000;\"><span style=\"font-size:13.6364px;line-height:23.9915px;\"><br />\r\n</span></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-size:14px;color:#000000;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;艺术家用酣畅的笔墨描绘剑门的四季，正如唐代著名政治家、文学家李德裕在《铭》中写道：</span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-size:14px;color:#000000;\"><br />\r\n</span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-size:14px;color:#FFA900;\">群山西来，波积云屯。</span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-size:14px;color:#FFA900;\">地险所会，斯为蜀门。</span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-size:14px;color:#FFA900;\">层岑峻壁，森若戈戟。</span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-size:14px;color:#FFA900;\">万壑奔东，双飞高阙。</span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-size:14px;color:#FFA900;\">翠岭中横，黯然黛色。</span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-size:14px;color:#FFA900;\">树若雄屏，以卫王国。</span>\r\n	</p>\r\n	<div style=\"text-align:center;\">\r\n		<span style=\"font-size:12px;line-height:1.5;\"></span>\r\n	</div>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826114245_57249.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;text-align:center;background-color:#FFFFFF;\">\r\n		<span style=\"font-size:12px;color:#000000;line-height:23.9915px;\">李清贵 &nbsp;&nbsp;剑门山韵 &nbsp;&nbsp;纸本&nbsp; 180X80cm &nbsp; 2010年</span><span style=\"font-size:12px;color:#000000;\"><span style=\"font-size:13.6364px;line-height:23.9915px;\"></span><span><span style=\"line-height:23.9915px;\"></span></span><span style=\"font-size:13.6364px;line-height:23.9915px;\"></span></span>\r\n	</p>\r\n	<div style=\"text-align:center;\">\r\n		<span style=\"font-size:12px;line-height:1.5;\"></span>\r\n	</div>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826114245_68524.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	李德广 &nbsp;剑门寒雪 &nbsp; 纸本 &nbsp;240X120cm &nbsp;2015年\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	&nbsp;花鸟画是中国画不能绕过的领域，此次参展艺术家借助竹和荷花表达直竿凌云、高风亮节情感，亦传承中国文人画的血脉精髓。\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826114245_71127.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	王锐 &nbsp;花鸟 &nbsp;纸本 &nbsp;68X68cm &nbsp; 2012年\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826114245_83666.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	李弘今 &nbsp;荷花 &nbsp;&nbsp;纸本 &nbsp;&nbsp;136X68cm &nbsp; 2012年\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826114245_67727.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;line-height:1.5;\">蒋凯 &nbsp;边塞风雪 &nbsp;国画 180X80cm &nbsp;2012年</span><span style=\"font-size:12px;line-height:1.5;\"></span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826114245_69350.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	杨帆 &nbsp;惊鸿 &nbsp;油画 &nbsp;120X60cm &nbsp;2015年\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826114245_31747.png\" alt=\"\" />\r\n</p>', null, null, '2', '距离2016第二届巴蜀国际艺术博览会开幕仅余十几天，经过组委会几个月的精心筹备，现已接收到并筛选出来自海内外优秀的艺术家、艺术机构、艺术产业园、画廊、艺术新媒介、非物质文化遗产的大量优质参展作品。其中，广元市美术家协会将展出11位优秀艺术家的作品。剑门关地处四川省广元市剑阁县，因唐代大诗人李白《蜀道难》中“剑阁峥嵘而崔嵬，一夫当关，万夫莫开”的赞誉名扬海内外，它集蜀道文化、三国文化、战争文化、红色文化于一体，有“天下第一雄关”之称。剑门山东南延伸绵延数百里，这里群峰突兀，山涛云海，成为艺术家笔下最爱勾勒的对象', '1429459200', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('42', '新都画院助力2016第二届巴蜀国际艺博会', '<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826115154_77189.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"><span style=\"line-height:22px;\">5月6日至9日，</span></span><span style=\"font-size:14px;\"><span style=\"line-height:22px;\">2016第二届巴蜀国际艺术博览会将于</span>成都市世纪城新国际会展中心隆重举行。随着开幕时间的临近，组委会正有序地进行最后的统筹和核对，经过组委会精心筛选出的来自海内外优秀的艺术家、艺术机构、艺术产业园、画廊、艺术新媒介、非物质文化遗产的大量优质参展作品即将呈现在观众面前。</span> \r\n</p>\r\n<p style=\"text-align:left;color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;text-indent:0em;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"> </span><span style=\"font-size:14px;\"> 其中，新都画院将展出8位优秀艺术家的作品，他们是：周平、谢德建、徐家华、李尤俊、危彬、朱白云、张大路、张国强。</span> \r\n</p>\r\n<p style=\"text-align:left;color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;text-indent:0em;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"> 新都画院自1993年9月创办以来，积极创作书画精品，<span style=\"line-height:22px;\">着重培养、扶持推出当地艺术人才，极大的推动了当地美术事业的发展。二十余年间，新都画院的画师们<span>一方面坚守住了传统书画的阵地，一方面也在与时俱进地发展。他们的画作中既有笔墨勾勒之间的古意，也在构图立意之中显露时代的痕迹。</span></span></span> \r\n</p>\r\n<strong> \r\n<div style=\"text-align:center;\">\r\n	<strong>新都画院 参展作品</strong> \r\n</div>\r\n</strong> \r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826115154_64945.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong>徐家华&nbsp;红梅花开</strong> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826115154_22159.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong>李尤俊&nbsp;花开时节</strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826115155_82531.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong>危彬&nbsp;幻境·金色的远方</strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826115155_63616.png\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;color:inherit;\">\r\n	<strong>张大路&nbsp;秋声图 96x88cm</strong> \r\n</p>\r\n<p style=\"text-align:center;color:inherit;\">\r\n	<strong><br />\r\n</strong> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826115155_52171.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong>张国强&nbsp;万物生 140cmx144cm 纸上水性颜料 2014年</strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/image/20160826/20160826115530_81485.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>', null, null, '2', '5月6日至9日，2016第二届巴蜀国际艺术博览会将于成都市世纪城新国际会展中心隆重举行。随着开幕时间的临近，组委会正有序地进行最后的统筹和核对，经过组委会精心筛选出的来自海内外优秀的艺术家、艺术机构、艺术产业园、画廊、艺术新媒介、非物质文化遗产的大量优质参展作品即将呈现在观众面前。其中，新都画院将展出8位优秀艺术家的作品，他们是：周平、谢德建、徐家华、李尤俊、危彬、朱白云、张大路、张国强。新都画院自1993年9月创办以来，积极创作书画精品，着重培养、扶持推出当地艺术人才，极大的推动了当地美术事业的发展。二十余年间，新都画院的画师们一方面坚守住了传统书画的阵地，一方面也在与时俱进地发展。他们的画作中既有笔墨勾勒之间的古意，也在构图立意之中显露时代的痕迹。', '1429372800', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('43', '成都市盐道街小学（东区）添彩2016首届巴蜀青少年艺博会', '<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826130927_27968.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\">5月6日到9日，2016首届巴蜀青少年艺术博览会将在成都市世纪城新国际会展中心举办。本届为青少年儿童艺术教育而服务的博览盛会，以“展示、创想、乐教、互动”为主元素，以“孩子•成长”、“创意•想象”为核心理念，充分展示成都市整体的艺术教育经验和成果。</span><span style=\"font-size:14px;line-height:22px;\">博览会旨在为成都市中小学校提供一个展示艺术教育特色的平台，为民营艺术教育机构搭建一个与大众交流的窗口，为青少年儿童提供一个全方位艺术学习体验平台。</span><span style=\"font-size:14px;line-height:1.6;\">参加活动的团体主要是公立的中小学教育机构、民营的社会艺术培训机构</span><span style=\"font-size:14px;line-height:1.6;\">。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826130927_70246.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	其中，成都市盐道街小学（东区）将携“剪纸艺术”，把本校的艺术教育成果展现在2016首届巴蜀青少年艺博会的舞台。\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826130927_98406.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	成都市盐道街小学（东区）成立于2004年6月，在以百年名校盐道街小学为核心学校的优质教育链发展平台中，秉承传承中创新，形成了“互动、互促、互惠”的办学格局，明确了“和乐教育”办学思想。在学校“和谐包容、快乐创享”的发展氛围中，以提升和乐艺体、提升学生综合素质，显现学校艺体教育特色，让学生崇尚运动、品鉴艺术、健康向上，打造成艺体气息浓郁、艺体成果显著的和乐校园。\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826130927_10892.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	近年来，盐道街小学（东区）硕果累累，先后获得“全国劳动技术先进学校”、“全国特色小队”、“成都市文明单位”、“成都市绿色学校”、“成都市红旗大队”、“成都市示范家长学校”、“锦江区校风示范学校”、“锦江区和谐校园”等荣誉称号，并成为锦江区首家“棋类人才培训基地”、“音乐教育实践基地”。在锦江教育“学在锦江、品质教育”发展目标引领下，在盐小优质教育链的聚合发展态势下，盐道街小学（东区）的稳定、快速、优效的发展态势已赢得社会的广泛赞誉。\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826130927_55422.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	\\<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826130927_72902.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826130927_51811.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826130927_30948.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826130927_50539.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826130928_86467.png\" alt=\"\" /> \r\n</p>', null, null, '2', '5月6日到9日，2016首届巴蜀青少年艺术博览会将在成都市世纪城新国际会展中心举办。本届为青少年儿童艺术教育而服务的博览盛会，以“展示、创想、乐教、互动”为主元素，以“孩子•成长”、“创意•想象”为核心理念，充分展示成都市整体的艺术教育经验和成果。博览会旨在为成都市中小学校提供一个展示艺术教育特色的平台，为民营艺术教育机构搭建一个与大众交流的窗口，为青少年儿童提供一个全方位艺术学习体验平台。参加活动的团体主要是公立的中小学教育机构、民营的社会艺术培训机构。', '1429286400', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('44', '四川巴蜀民歌传习所增色2016第二届巴蜀国际艺博会', '<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826131923_42879.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;line-height:1.5;\">\r\n	<p style=\"text-align:left;color:#0C0C0C;\">\r\n		<span style=\"color:#3E3E3E;font-size:14px;\"><span style=\"line-height:22px;\"> 5月6日至9日，</span></span><span style=\"color:#3E3E3E;font-size:14px;\"><span style=\"line-height:22px;\">2016第二届巴蜀国际艺术博览会将于</span>成都市世纪城新国际会展中心隆重举行。随着开幕时间的临近，组委会正有序地进行最后的统筹和核对，经过组委会精心筛选出的来自海内外优秀的艺术家、艺术机构、艺术产业园、画廊、艺术新媒介、非物质文化遗产的大量优质参展作品即将呈现在观众面前。</span>\r\n	</p>\r\n	<p style=\"text-align:left;color:#0C0C0C;\">\r\n		<span style=\"color:#3E3E3E;font-size:14px;\"><br />\r\n</span>\r\n	</p>\r\n	<p style=\"text-align:left;color:#0C0C0C;\">\r\n		<span style=\"color:#3E3E3E;font-size:14px;\"> 其中，四川巴蜀民歌传习所将展出多副饱含巴蜀民歌风情的书画作品。<br />\r\n</span>\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		四川巴蜀民歌传习所\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		成立于2014年，属四川省文联主管的民非组织。主要经营巴蜀民歌综合艺术(书画影响等）的采风、挖掘、整理，竞技比赛，表演交流，传承教育和传扬。\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		天籁民歌，千口百韵锤炼的最美诗，为劳动抒写、为生活抒情、为生命抒怀。巴蜀民歌斑斓野逸灵趣是中华民族音乐绚丽的奇葩，正随现代化进程迅疾尘封消逝。\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		趁见证传统人文精髓的艺术家尚在，艺出多样“生活中来生活中去”，“图文并茂声色一体”抢救性绘转、体验性推陈，受益当代、传诸后世、纷呈世界，至为使命责任。\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:justify;color:#0C0C0C;\">\r\n		<span style=\"color:#3E3E3E;font-size:14px;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<span style=\"color:#FFFFFF;\"><strong><span style=\"background-color:#4AA9AA;\">四川巴蜀民歌传习所参展作品</span></strong></span><span style=\"font-size:12px;line-height:1.5;\"></span>\r\n	</p>\r\n</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;line-height:1.5;\"></span><img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826131923_12285.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	大河涨水沙浪沙　邓由怀&nbsp;\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826131923_22988.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	嘎足碉楼十二层　邓由怀&nbsp;\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826131924_85756.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	隔河看见一只鸡　王双才&nbsp;\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826131924_74970.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	龚鹏程　题诗\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826131924_84586.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	好窝映山红　冯祥伦&nbsp;\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826131924_90675.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<p style=\"color:inherit;font-size:14px;\">\r\n		江头客人去江尾　高澄&nbsp;\r\n	</p>\r\n	<p style=\"color:inherit;font-size:14px;\">\r\n		<br />\r\n	</p>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826131924_91647.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	莫嫌家乡山无柴　任雅林&nbsp;\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826131924_23656.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<p style=\"color:inherit;font-size:14px;\">\r\n		牛儿苦　高澄&nbsp;\r\n	</p>\r\n	<p style=\"color:inherit;font-size:14px;\">\r\n		<br />\r\n	</p>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826131924_75536.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	听山歌　朱宝勇&nbsp;\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826131924_14070.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	长江万里多艰险　孙适舟&nbsp;\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826131924_32988.png\" alt=\"\" />\r\n</p>', null, null, '2', '5月6日至9日，2016第二届巴蜀国际艺术博览会将于成都市世纪城新国际会展中心隆重举行。随着开幕时间的临近，组委会正有序地进行最后的统筹和核对，经过组委会精心筛选出的来自海内外优秀的艺术家、艺术机构、艺术产业园、画廊、艺术新媒介、非物质文化遗产的大量优质参展作品即将呈现在观众面前。其中，四川巴蜀民歌传习所将展出多副饱含巴蜀民歌风情的书画作品。', '1429200000', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('45', '11幅毕加索著名“公牛”系列作品离奇失窃', '<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826132458_63717.png\" alt=\"\" /><span style=\"font-size:12px;line-height:1.5;\"></span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:12px;\"><span style=\"line-height:1.6;\">德国银行Portigon位于杜塞尔多夫的总部</span></span>\r\n</p>\r\n<p style=\"text-align:left;color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">包括11幅石版画在内的毕加索作品日前被盗。这些作品曾经收藏在名为Portigon AG的德国银行内。该银行位于北莱茵–威斯特法伦州。</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"> Portigon 成立于2012年，是西徳州立银行WestLB的合法继承公司。公司的发言人于4月15日确认公司遭到盗窃。</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826132458_63193.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	971年10月，毕加索在法国穆然\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\">据德国媒体Spiegel报道，这次被盗作品总价值110万欧元(约合人民币807万元)，但这些作品的市场价值可能会更高。除了被盗的11幅来自毕加索著名的公牛系列石版画外，表现主义艺术家加百列·穆特尔的一幅绘画也在被盗作品之列。</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"> Spiegel同时报道，早在2014年冬，公司员工就已经发现，特别用于储存艺术品的保险库在非正常时间段内被打开过。随即展开的内部调查显示，12件收藏中的作品被盗。</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"> 银行当即发出声明并告通知了州政府，但由于该起案件始终未对外披露，警方于2016年初终结了对此案的调查。</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"> 发言人称：“调查没有查出任何具有动机的可疑人士。\"据悉，被盗作品已在警方备案。</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"> 据德国之声报道，这批被盗的作品在被收藏进保险库前曾经用于装饰前WestLB银行分部。这家州立银行于金融危机间破产，此后由Portigon继承。</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"> 这一些列藏品早在2014年就引起轰动。当时公司想要出售380件包括约瑟夫·博伊斯，格哈德·里希特，西格马·波尔克以及跟奥古斯特·马科等艺术家在内的作品。</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"> 这场销售引起了德国艺术史协会的极力反对。该协会担心此类艺术收藏的销售会被州政府用来抵债。</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"> Portigon银行最终同意把这批上百万美金的藏品留在州内，并最终将作品卖给了一家州立基金会。</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826132458_60855.png\" alt=\"\" />\r\n</p>', null, null, '2', '包括11幅石版画在内的毕加索作品日前被盗。这些作品曾经收藏在名为Portigon AG的德国银行内。该银行位于北莱茵–威斯特法伦州。\r\n　　Portigon 成立于2012年，是西徳州立银行WestLB的合法继承公司。公司的发言人于4月15日确认公司遭到盗窃。', '1429113600', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('46', '2016首届巴蜀青少年艺博会活动日程安排', '<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826132857_24054.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	在成都市博览局、成都市教育局的支持下，2016首届巴蜀青少年艺博会将于5月6日上午9：30在成都新会展中心8号馆举行开幕式，展期至5月9日结束。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	今\r\n年，巴蜀艺博会组委会决定增加“巴蜀青少年艺博会”，首次把青少年艺术的展览展示作为独立的展会来呈现，首次提出“巴蜀双艺博”的概念。其理由是：青少年\r\n作为文化艺术的传承者，作为文化艺术事业的接班人，如此重要的展会活动不可以缺席！如果纳入巴蜀国际艺博会混合展示，有些混淆，如果独立展示，就会更有青\r\n少年艺术成果的整体形象。因此，组委会慎重决定，将巴蜀青少年艺术板块独立呈现，以“巴蜀青少年艺博会”的形式出现，这一决定得到了成都市教育局和成都市\r\n博览局的支持。我们希望，参观者看到的不仅有国际舞台上的艺术精品，也有国内主要是巴蜀地区的艺术代表作品，还有青少年的艺术成果展示，内容丰富，国际国\r\n内，老中青少幼，精彩纷呈。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n相关活动安排<br />\r\n1首届巴蜀艺术教育论坛\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	首届巴蜀艺术教育论坛将于2016年5月8日上午九点半在世纪城新会展中心假日酒店锦江厅举行。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	巴\r\n蜀艺术教育论坛作为首届巴蜀青少年艺博会期间的重要活动，备受参展机构的关注。艺术教育的理念问题一直是困扰艺术教育的重大问题，明确了艺术教育的理念，\r\n就不会出原则和方向问题。正是基于这样的考虑，首届巴蜀青少年艺博会同期举办巴蜀艺术教育论坛，宗旨就是梳理艺术教育的核心理念和带有规律性的教育观念问\r\n题。因此，组委会邀请到了全国知名的艺术教育专家出席论坛，做精彩的主题演讲，值得期待。\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n2首届童星耀巴蜀才艺汇演\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826132857_61289.png\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	首届童星耀巴蜀才艺汇演集中在5月7日、8日举办，舞台设在新会展中心8号馆，实行开放式的舞台表演形式，届时，将有一批艺术特色学校的学生节目登台亮相，助阵巴蜀青少年艺博会。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	首届童星耀巴蜀才艺汇演是巴蜀青少年艺博会的重要活动。该活动主要呈现青少年的才艺表演，包括声乐、器乐、舞蹈、武术、小品、书画、表演等才艺展示，组委会希望将“童星耀巴蜀才艺汇演”办成一年一度的“巴蜀青少年才艺春晚”。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"color:#4C4C4C;font-size:14px;\">\r\n	主办单位：\r\n</p>\r\n<p style=\"color:#4C4C4C;font-size:14px;\">\r\n	四川省美协、四川省书协、四川省巴蜀画派促进会\r\n</p>\r\n<p style=\"color:#4C4C4C;font-size:14px;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#4C4C4C;font-size:14px;\">\r\n	承办单位：\r\n</p>\r\n<p style=\"color:#4C4C4C;font-size:14px;\">\r\n	四川巴蜀画派青少年艺术交流中心、四川巴蜀画派专修学院\r\n</p>\r\n<p style=\"color:#4C4C4C;font-size:14px;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#4C4C4C;font-size:14px;\">\r\n	协办单位：\r\n</p>\r\n<p style=\"color:#4C4C4C;font-size:14px;\">\r\n	成都市教育学会、成都市民办教育协会、成都市美协、成都市书协\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826132857_96668.png\" alt=\"\" /> \r\n</p>', null, null, '1', '在成都市博览局、成都市教育局的支持下，2016首届巴蜀青少年艺博会将于5月6日上午9：30在成都新会展中心8号馆举行开幕式，展期至5月9日结束。\r\n今年，巴蜀艺博会组委会决定增加“巴蜀青少年艺博会”，首次把青少年艺术的展览展示作为独立的展会来呈现，首次提出“巴蜀双艺博”的概念。其理由是：青少年作为文化艺术的传承者，作为文化艺术事业的接班人，如此重要的展会活动不可以缺席！如果纳入巴蜀国际艺博会混合展示，有些混淆，如果独立展示，就会更有青少年艺术成果的整体形象。因此，组委会慎重决定，将巴蜀青少年艺术板块独立呈现，以“巴蜀青少年艺博会”的形式出现，这一决定得到了成都市教育局和成都市博览局的支持。我们希望，参观者看到的不仅有国际舞台上的艺术精品，也有国内主要是巴蜀地区的艺术代表作品，还有青少年的艺术成果展示，内容丰富，国际国内，老中青少幼，精彩纷呈。', '1429027200', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('47', '2016第二届巴蜀国际艺博会活动日程安排', '<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826133430_46021.png\" alt=\"\" /><span style=\"font-size:12px;line-height:1.5;\"></span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<p style=\"text-align:left;color:#0C0C0C;text-indent:2em;\">\r\n		在中国国际文化交流中心、中共四川省委宣传部、四川省文学艺术界联合会、成都市博览局的关心指导下，中国画院、四川省诗书画院的支持下，2016第二届巴蜀国际艺博会将于5月6日上午9:30在成都新会展中心7、8号馆举行开幕式，展期将持续到5月9日。\r\n	</p>\r\n	<p style=\"color:#0C0C0C;text-indent:2em;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#0C0C0C;\">\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<strong>★ &nbsp;</strong><strong class=\"135brush\">相关活动安排</strong><strong>&nbsp;&nbsp;★</strong>\r\n	</p>\r\n	<p style=\"color:#0C0C0C;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#0C0C0C;\">\r\n		<br />\r\n	</p>\r\n	<div style=\"text-align:left;\">\r\n		<span style=\"font-size:12px;line-height:1.5;\">第二届巴蜀艺术金融论坛</span><span style=\"font-size:12px;line-height:1.5;\"></span>\r\n	</div>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826133430_96022.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	2015第一届中国巴蜀艺术金融发展论坛 嘉宾合影\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<p style=\"color:#0C0C0C;text-indent:2em;\">\r\n		2016第二届巴蜀艺术金融论坛将于5月7日下午两点在锦江区东大街明宇豪雅饭店举行。\r\n	</p>\r\n	<p style=\"color:#0C0C0C;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#0C0C0C;text-indent:2em;\">\r\n		第\r\n二届巴蜀艺术金融论坛是巴蜀国际艺博会期间的重要活动，汇集国内外顶级艺术金融家、实战家、艺术品市场营销专家、艺术品网络平台、艺术品金融机构、文交\r\n所、艺术基金、艺术品投资机构等负责人才华与智慧，集合艺术品投融资及市场营销多种解决方案的集大成者。这是一个最坏的时代，“互联网+艺术金融”给艺术\r\n品市场带来一场颠覆式的变革；这也是一个最好的时代，从2009年7月我国第一部文化产业专项规划——《文化产业振兴规划》由国务院常务会议审议通过，至\r\n今的八年时间里，将振兴文化产业提升至国家战略高度，并调动政策资源、经济资源及外交资源予以大力支持。第二届巴蜀艺术金融论坛在时代的大背景下，以“互\r\n联网+艺术金融”为市场导向，以融合传统和创新为经营理念，嫁接艺术和金融两大产业板块，为文化艺术的繁荣把脉引航。\r\n	</p>\r\n	<p style=\"color:#0C0C0C;\">\r\n		<br />\r\n	</p>\r\n2016宝墩春季拍卖会\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826133430_26658.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	2015四川宝墩秋季拍卖会\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<p style=\"color:#0C0C0C;text-indent:2em;\">\r\n		2016宝墩春季拍卖会将于2016年5月8日下午两点在世纪城新会展中心假日酒店锦江厅举槌。\r\n	</p>\r\n	<p style=\"color:#0C0C0C;text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#0C0C0C;text-indent:2em;\">\r\n		&nbsp;&nbsp;\r\n2016宝墩春季拍卖会作为巴蜀国际艺博会的重要活动，拍卖作品以反应巴蜀特色的优秀作品为主，深蕴地域文化气质；尺幅则以一平尺和扇面书画作品为主，价\r\n格实惠，旨在拉近消费者和艺术品市场的距离，让更多市民可以走进上乘的艺术品。为了能在西南地区培育一个规范且具有特色的书画艺术品交易市场，巴蜀国际艺\r\n博会组委会计划自2016年起，每年举行以“品鉴巴蜀艺术”为主题的专场拍卖会，为各位艺博会参展机构和艺术家提供一个直接面向市场的舞台。\r\n	</p>\r\n	<p style=\"color:#0C0C0C;text-indent:2em;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#4C4C4C;font-size:14px;\">\r\n		主办单位：\r\n	</p>\r\n	<p style=\"color:#4C4C4C;font-size:14px;text-indent:2em;\">\r\n		中华美网、四川国际文化交流中心、四川省美术家协会、四川省书法家协会、四川省巴蜀画派促进会、成都国家广告产业园\r\n	</p>\r\n	<p style=\"color:#4C4C4C;font-size:14px;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#4C4C4C;font-size:14px;\">\r\n		承办单位：\r\n	</p>\r\n	<p style=\"color:#4C4C4C;font-size:14px;text-indent:2em;\">\r\n		四川巴蜀画派文化产业集团\r\n	</p>\r\n	<p style=\"color:#4C4C4C;font-size:14px;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#4C4C4C;font-size:14px;\">\r\n		协办单位：\r\n	</p>\r\n	<p style=\"color:#4C4C4C;font-size:14px;text-indent:2em;\">\r\n		四川文化产业职业学院、四川新闻网传媒集团股份有限公司、四川宝墩拍卖有限公司、四川宝墩艺术品有限公司、四川巴蜀画派传媒有限公司、四川巴蜀画派专修学院\r\n	</p>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826133430_46974.png\" alt=\"\" />\r\n</p>', null, null, '1', '在中国国际文化交流中心、中共四川省委宣传部、四川省文学艺术界联合会、成都市博览局的关心指导下，中国画院、四川省诗书画院的支持下，2016第二届巴蜀国际艺博会将于5月6日上午9:30在成都新会展中心7、8号馆举行开幕式，展期将持续到5月9日。\r\n\r\n', '1428940800', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('48', '创出天价的中国油画', '<p style=\"text-align:left;\">\r\n	《周庄》成交后，藏界为之欣喜。那么在《周庄》之前，中国油画的拍卖价格纪录又曾被哪些作品打破？它们的成交价又各是多少呢？\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:14px;\">徐悲鸿《放下你的鞭子》</span></strong>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826134107_60688.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	2007年4月7日，徐悲鸿的油画《放下你的鞭子》在香港苏富比拍卖会上以7200万港元（约合7128万元人民币）成交，创下中国油画的拍卖价格纪录。《放下你的鞭子》本是著名剧作家田汉根据德国作家歌德小说改编而成的独幕剧，后被电影导演陈鲤庭执笔改编成抗战街头剧。油画《放下你的鞭子》是徐悲鸿创作的唯一一幅抗战题材作品，意义非同一般；且此画诞生于画家艺术创作的鼎盛时期，因而几无瑕疵。\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<strong>陈逸飞《山地风》</strong>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826134107_80323.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\">2011年5月24日，在北京举行的中国嘉德油画专场上，陈逸飞代表作《山地风》以8165万元成交，打破了中国油画拍卖价格纪录。陈逸飞创作了一系列以西藏民俗为主题的油画，《山地风》便是其西藏题材中最为重要的作品，也是陈逸飞整个艺术生涯中最为重要的作品。</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;该作曾于1994年中国嘉德秋季拍卖会上以286万人民币打破了当时中国油画拍卖价格纪录，17年后再现拍场则再破纪录。</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"></span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:14px;\">常玉《五裸女》</span></strong>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826134107_98541.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	出乎人们意料的是，《山地风》的成交纪录在一周内就被另一幅油画打破。2011年5月30日，在香港的罗芙奥现代艺术专场上，常玉的《五裸女》以1.28亿港元（约合1.07亿元人民币）的价格刷新了中国油画的拍卖价格纪录。常玉是与徐悲鸿同时代的旅法画家，历年拍场上的常玉作品主要为花卉题材，鲜有以裸女为主题的作品。目前已知的常玉裸女油画不过51幅，多数为卧姿，而《五裸女》则为站姿，且是常玉最大幅裸女油画，故而吸引了一些买家入场。\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:14px;\">\r\n	<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n		<strong><span style=\"font-size:14px;\">曾梵志《最后的晚餐》</span></strong>\r\n	</p>\r\n</span></strong>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826134107_12905.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n		<span style=\"font-size:14px;\">2013年10月5日，香港苏富比秋拍上，曾梵志《最后的晚餐》以1.8亿港元成交。这幅油画是一幅当代艺术作品，其内容被多家媒体释义为“少先队员排排坐吃西瓜”。尽管这幅画当时打破了纪录，但也引起了质疑，有业内人士分析道：“如此高价只是其收藏者尤伦斯男爵自娱自乐的炒作罢了”。鉴于这幅画的本身价值，画家曾梵志并未在此后获得业界的持续关注，其天价代表作《面具》系列至今已鲜有人提及。</span>\r\n	</p>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826134107_65335.png\" alt=\"\" />\r\n</p>', null, null, '2', '《周庄》成交后，藏界为之欣喜。那么在《周庄》之前，中国油画的拍卖价格纪录又曾被哪些作品打破？它们的成交价又各是多少呢？\r\n\r\n\r\n', '1428854400', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('49', '2016“巴蜀双艺博”下月开幕 展会筹备紧张有序', '<p>\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826134733_52495.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	4月10日，2016第二届巴蜀国际艺术博览会和2016首届巴蜀青少年艺术博览会组委会召开了本届“巴蜀双艺博”筹备会议，展会筹备工作正式进入倒计时。组委会全体成员参加了此次会议。\r\n</p>\r\n<p>\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826134733_46518.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		本次会议上，2016第二届巴蜀国际艺术博览会组委会主任、2016首届巴蜀青少年艺博会组委会主任<a href=\"http://www.zhonghuameiwang.com/artist/202.shtml\" target=\"_blank\">李杰</a>对本届“巴蜀双艺博”组织机构情况、组委会各部门工作内容进行了梳理，安排好各个岗位的对应负责人，全力做好“巴蜀双艺博”期间的各项工作，确保展会以及展会期间各项活动的顺利举行。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		四川宝墩拍卖有限公司常务副总经理、巴蜀国际艺术博览会组委会副主任马琳针对此次艺博会期间的相关活动特别提到， 5月7日举行的中国巴蜀艺术金融发展论坛将以“全民艺术，全民金融”为主题。届时将邀请到著名艺术家<a href=\"http://www.zhonghuameiwang.com/artist/167.shtml\" target=\"_blank\">高小华</a>、艺团网创始人、艺术品份额交易创始团队阿特多多负责人以及金融高管和政府官员同场畅谈最新产业理念和发展模式；而5月8日进行的巴蜀艺博会宝墩春季拍卖会已确认五十余件拍品，拍卖师人选已敲定，将为各参展单位提供一个直接面向市场的舞台。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		四川宝墩艺术品有限公司常务副总经理、宝墩艺术馆馆长、巴蜀国际艺术博览会组委会副主任吴晓敏表示，展会的招商招展工作已基本完成，下个星期将反复核对、全面落实最终细节，展会筹备最终阶段的有序进行将有力保证“巴蜀双艺博”的顺利召开。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		四川巴蜀画派传媒有限公司常务副总经理、巴蜀国际艺术博览会组委会秘书长王荀表示，此次“无缝艺术展墙”已准备到位，并首次提出“双艺博”的概念，将巴蜀国际艺术博览会和首届巴蜀青少年艺博会联合承办，相信届时将带给各参展商以及观众全新的体验。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		四川中华美网信息服务有限公司副总经理、巴蜀国际艺术博览会组委会副秘书长姚守国讲到，将在“巴蜀双艺博”期间举行的收藏讲座已经邀请到艺术品商会、收\r\n藏家、美术馆、博物馆、艺术媒体等艺术界人士200余人，届时将共同探讨艺术品收藏在当前社会经济形势下，如何进行更科学理性的收藏。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		四川巴蜀画派青少年艺术交流中心主任、巴蜀青少年艺博会组委会副主任兼秘书长王迪表示，展会筹备工作正在落实最终细节，将在近期把展会完整情况对外发布。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		本届“巴蜀双艺博”，将于2016年5月6日至9日在成都市世纪城新国际会展中心隆重举行。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<span style=\"color:#C00000;\"><strong>附录：</strong></span>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<span style=\"color:#0070C0;\"><strong>2016第二届巴蜀国际艺术博览会</strong></span>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<span style=\"color:#C00000;\">组织机构：</span>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		由中国国际文化交流中心、中共四川省委宣传部、四川省文化厅、四川省文学艺术界联合会、四川省商务厅、四川博览事务局、成都市博览局指导，中国画院、四川省诗书画院支持，中华美网、四川国际文化交流中心、四川省美术家协会、四川省书法家协会、四川省巴蜀画派促进会、成都<a href=\"http://www.zhonghuameiwang.com/artist/56.shtml\" target=\"_blank\">国家</a>广告产业园联合主办，四川巴蜀画派文化产业集团承办，四川文化产业职业学院、四川新闻网传媒集团股份有限公司、四川宝墩拍卖有限公司、四川宝墩艺术品有限公司、四川巴蜀画派传媒有限公司、四川巴蜀画派专修学院协办。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<span style=\"color:#C00000;\">组委会：</span>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong>主任：</strong>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<a href=\"http://www.zhonghuameiwang.com/artist/202.shtml\" target=\"_blank\">李杰</a>（四川省巴蜀画派促进会常务副会长兼秘书长、四川巴蜀画派文化产业集团董事长、中华美网总编辑）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong>联席主任：</strong>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<a href=\"http://www.zhonghuameiwang.com/artist/139.shtml\" target=\"_blank\"></a><a href=\"http://www.zhonghuameiwang.com/artist/140.shtml\" target=\"_blank\">梁时民</a>（四川省美术家协会常务副主席兼秘书长、四川美术馆馆长）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		戴跃（四川省书法家协会常务副主席兼秘书长）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		李小江（四川国际文化交流中心秘书长）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		刘移（成都<a href=\"http://www.zhonghuameiwang.com/artist/56.shtml\" target=\"_blank\">国家</a>广告产业园董事长）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong>副主任：</strong>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		彭浩（成都<a href=\"http://www.zhonghuameiwang.com/artist/56.shtml\" target=\"_blank\">国家</a>广告产业园总经理）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		马琳（四川宝墩拍卖有限公司常务副总经理）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		吴晓敏（四川宝墩艺术品有限公司常务副总经理、宝墩艺术馆馆长）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong>秘书长：</strong>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		王荀（四川巴蜀画派传媒有限公司常务副总经理）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong>副秘书长：</strong>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		李鹍（四川巴蜀画派文化产业集团行政部副主任）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		姚守国（四川中华美网信息服务有限公司副总经理）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<span style=\"color:#0070C0;\"><strong>2016首届巴蜀青少年艺术博览会</strong></span>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<span style=\"color:#C00000;\">组织机构：</span>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		由成都市博览局、成都市教育局、成都市文学艺术界联合会支持举办，四川省美协、四川省书协、四川省巴蜀画派促进会主办，四川巴蜀画派青少年艺术交流中心承办，成都市美协、成都市书协、成都市教育学会、成都市民办教育协会协办。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<span style=\"color:#C00000;\">组委会：</span>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong>主任：</strong>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<a href=\"http://www.zhonghuameiwang.com/artist/202.shtml\" target=\"_blank\">李杰</a>（四川省巴蜀画派促进会常务副会长兼秘书长、四川巴蜀画派文化产业集团董事长、中华美网总编辑）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong>联席主任：</strong>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<a href=\"http://www.zhonghuameiwang.com/artist/139.shtml\" target=\"_blank\"></a><a href=\"http://www.zhonghuameiwang.com/artist/140.shtml\" target=\"_blank\">梁时民</a>（四川省美术家协会常务副主席兼秘书长、四川美术馆馆长）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		戴跃（四川省书法家协会常务副主席兼秘书长）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong><br />\r\n</strong>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong>副主任：</strong>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		刘正兴（四川省美术家协会副主席、成都市美术家协会主席）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<a href=\"http://www.zhonghuameiwang.com/artist/32.shtml\" target=\"_blank\">舒炯</a>（四川省书法家协会副主席、成都市书法家协会主席）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		王迪（四川巴蜀画派青少年艺术交流中心主任）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong>秘书长（兼）：</strong>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		王迪（四川巴蜀画派青少年艺术交流中心主任）\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong>副秘书长：</strong>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		杜欣（四川巴蜀画派专修学院办公室主任）\r\n	</p>\r\n</p>', null, null, '1', '4月10日，2016第二届巴蜀国际艺术博览会和2016首届巴蜀青少年艺术博览会组委会召开了本届“巴蜀双艺博”筹备会议，展会筹备工作正式进入倒计时。组委会全体成员参加了此次会议。\r\n本次会议上，2016第二届巴蜀国际艺术博览会组委会主任、2016首届巴蜀青少年艺博会组委会主任李杰对本届“巴蜀双艺博”组织机构情况、组委会各部门工作内容进行了梳理，安排好各个岗位的对应负责人，全力做好“巴蜀双艺博”期间的各项工作，确保展会以及展会期间各项活动的顺利举行。\r\n\r\n \r\n\r\n　　四川宝墩拍卖有限公司常务副总经理、巴蜀国际艺术博览会组委会副主任马琳针对此次艺博会期间的相关活动特别提到， 5月7日举行的中国巴蜀艺术金融发展论坛将以“全民艺术，全民金融”为主题。届时将邀请到著名艺术家高小华、艺团网创始人、艺术品份额交易创始团队阿特多多负责人以及金融高管和政府官员同场畅谈最新产业理念和发展模式；而5月8日进行的巴蜀艺博会宝墩春季拍卖会已确认五十余件拍品，拍卖师人选已敲定，将为各参展单位提供一个直接面向市场的舞台。', '1428768000', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('50', '第二届巴蜀国际艺博会将于5月6日 在成都新会展中心隆重开幕 招商招展工作紧张进行', '<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826135220_90840.jpg\" alt=\"\" /><span style=\"font-size:12px;line-height:1.5;\"> </span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	由中共四川省委宣传部、四川省文化厅、四川省文联、四川省商务厅、四川博览事务局、成都市博览局指导，四川省诗书画院、四川省美术家协会、四川省书法家协会支持，中华美网、四川省巴蜀画派促进会、成都<a href=\"http://zhonghuameiwang.com/artist/56.shtml\" target=\"_blank\">国家</a>广告产业园联合主办，四川巴蜀画派文化产业集团承办，四川宝墩拍卖有限公司、四川宝墩艺术品有限公司协办的2016巴蜀国际艺术博览会将于2016年5月6日至9日在成都市世纪城新国际会展中心隆重举行。\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826135220_25292.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		1月16日下午，主办方召开新闻发布会，四川省美术家协会常务副主席兼秘书长、四川美术馆馆长、四川省巴蜀画派促进会副会长<a href=\"http://zhonghuameiwang.com/artist/139.shtml\" target=\"_blank\"></a><a href=\"http://zhonghuameiwang.com/artist/140.shtml\" target=\"_blank\">梁时民</a>，四川省巴蜀画派促进会常务副会长兼秘书长、四川巴蜀画派文化产业集团董事长李杰，成都<a href=\"http://zhonghuameiwang.com/artist/56.shtml\" target=\"_blank\">国家</a>广告产业园总经理彭浩出席新闻发布会。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<a href=\"http://zhonghuameiwang.com/artist/139.shtml\" target=\"_blank\"></a><a href=\"http://zhonghuameiwang.com/artist/140.shtml\" target=\"_blank\">梁时民</a>在\r\n发布会上表示，省美协和美术馆将给予巴蜀国际艺术博览会大力支持，他说：“巴蜀艺博会的举办贯彻落实了习总书记在文艺座谈会上的讲话精神，是对我国文化战\r\n略和文化发展新常态时期的一种大力推动，是对四川文化产业发展的有益尝试，值得肯定。四川在艺术成就、自然景观、人文历史等方面在全国乃至世界都有很多亮\r\n点值得推广和展示，我国第一个官办画院——翰林图画院五代时期就设立在成都，为四川和中国的绘画艺术奠定了坚实的基础。自四川省委提出创建巴蜀画派以来，\r\n为推动四川文化事业、文化产业的发展，实现文化强省的目标，巴蜀画派促进会抓住契机，通过七年时间的坚持和努力，大力推动了四川美术事业的发展。2015\r\n年首届巴蜀国际艺博会取得了不俗成绩，2016年巴蜀艺博会组委会的队伍更加强大，目前已邀请到4个省的文联机构、18家国际艺术机构参展。巴蜀艺博会不\r\n仅为四川艺术家，还为全国艺术家搭建了展示的机会和平台。2016巴蜀艺博会将展示更多接地气的艺术品，让老百姓接近艺术家、了解艺术家，正如习总书记所\r\n说，艺术要为广大人民群众服务。作为四川省美协的一名管理者，我全力支持2016年巴蜀艺博会的举办，也会动员全省的美术家积极参与，为推动四川文化事业\r\n的发展做出贡献，为艺术家能创造出接地气、人民喜欢、有“高峰”的艺术作品做出贡献。”\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong><span style=\"color:#C00000;\">专业展陈：展览面积增加一倍，美术作品展陈更佳</span></strong>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		巴蜀艺博会组委会主任李杰介绍，2016巴蜀国际艺术博览会的展馆面积比2015年增加了一倍多，展馆在原来的3号馆基础上增加了4号馆，加上两个连接馆，艺博会展馆总面积达到2.4万平方米。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		为了进一步满足美术艺术精品的展陈效果，组委会从北京定制了更适合美术作品展陈的展板，也邀请到了专业的布展团队，展陈效果将会更佳。2016巴蜀艺博会将以更加专业、科学、合理的方式呈现在观众面前。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<span style=\"color:#C00000;\"><strong>精品荟萃：国际元素 时空交错的黄金地带</strong></span>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		巴蜀艺博会坚持“专业化、民族化、国际化”的办展方针，突出展示当代杰出的美术艺术作品、最优秀的艺术机构和画廊、最有市场潜力和价值的艺术家、艺术教\r\n育、艺术产业成就、工艺美术等重点文化艺术产业领域和代表行业水平的文化艺术企业、产品和项目，突出国际性和综合性的文化艺术产业盛会优势，将更多专业领\r\n域的优质会展资源和艺术金融理念注入艺博会，采用高端定制服务理念吸引更多优质观展人员入场交流和交易，引领艺术产业未来发展趋势，塑造巴蜀艺博会专业化\r\n的国际品牌形象。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		截止目前，巴蜀艺博会组委会已收到来自美国、加拿大、英国、法国、俄罗斯、西班牙、哈萨克斯坦等<a href=\"http://zhonghuameiwang.com/artist/56.shtml\" target=\"_blank\">国家</a>和地区的18个机构参展确认，和去年12个<a href=\"http://zhonghuameiwang.com/artist/56.shtml\" target=\"_blank\">国家</a>和\r\n地区的参展机构相比，国际化元素更加多样和丰富。此外，安徽省文联、江苏省文联、云南省文联和重庆市文联四家机构都以文联组织牵头形式参加巴蜀国际艺博\r\n会，北京、上海、山东、陕西、甘肃、江苏、浙江的画廊和艺术家纷纷表示将参加巴蜀艺博会，河南省博物馆和安阳博物馆将组织精美展品参加，为巴蜀观众呈现中\r\n原文明的绚丽华章。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong><span style=\"color:#C00000;\">民族集结：浓郁的地方特色凸显传统文化魅力</span></strong>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		民族文化艺术体现了普通劳动者们的聪明和智慧。它们立足于本土本乡，注意传统特色和时代特征的结合，根据时代与社会变迁情形对其进行提炼创新，使其顺应市场的经济环境。2016巴蜀国际艺术博览会因为注入了传统艺术所以更具价值，主要体现为：\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<span style=\"color:#C00000;\">一是优秀艺术作品对话国宝大熊猫</span>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		世界上大熊猫数量已经少于1000只，其中80%分布在四川地区，而成都则因为这些大熊猫闻名全球。成都大熊猫繁育研究中心距离成都市中心仅仅6公里，\r\n观赏这些在自然栖息地的美丽生物十分便利。2016巴蜀艺博会融入“熊猫”元素，将优秀书画艺术家的熊猫作品奉献给大众，为巴蜀艺博会覆盖上美丽的自然气\r\n息，让国际国内参展机构和艺术家踏上一次难忘的文化与自然之旅。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<span style=\"color:#C00000;\">二是中国四大年画集体亮相精彩纷呈</span>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		绵竹年画是四川特色，它们是汉民族优秀的传统工艺，也是世世代代民间手工艺人勤劳和智慧的结晶，体现了巴蜀人民乐观向上的思想感情和古老的汉民族风尚，素有“四川三宝”、“绵竹三绝”之美誉，多以木版印出轮廓而后填色。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		绵竹年画天津杨柳青年画、山东潍坊杨家埠木版年画、苏州桃花坞木版年画齐名为中国四大年画。这四大年画都将整体呈现在巴蜀艺博会上，为四川人民奉献传统艺术的独特之美。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<span style=\"color:#C00000;\">三是蜀锦蜀绣齐聚一堂阐释巴蜀文化魅力</span>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		蜀锦、蜀绣也将来到新会展，蜀锦具有古老的民族传统风格、浓郁的四川地方特色、厚重的历史文化底蕴，是中国染织传统工艺的重要组成部分，蜀锦图案在中国\r\n工艺美术图案中，占有十分瑰丽的篇章，对中国后世锦缎染织图案的发展，具有承先启后的巨大而深刻的影响。而蜀绣是中国的传统工艺，集中于四川成都，在晋代\r\n被称蜀中之宝，显著的特点是有一气呵成，气韵连贯。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<span style=\"color:#C00000;\">四是文房四宝的艺术性和实用性完美呈现</span>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		以安徽省文联为代表的参展团队将整体呈现纸笔墨砚文房四宝的独特魅力，展现徽派文化的传承与价值。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		夹江宣纸为四川特色元素，有着其它纸张不可替代的品质，洁白绵密，浸润保墨。早在上个世纪初，夹江宣纸就因其洁白绵密，被张大千将其与安徽宣纸一道并称为“国之二宝”。夹江竹纸制作技艺被列入首批<a href=\"http://zhonghuameiwang.com/artist/56.shtml\" target=\"_blank\">国家</a>非物质文化遗产名录。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong><span style=\"color:#C00000;\">四天展期：四大展区上演文化艺术盛宴</span></strong>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		据了解，2016巴蜀国际艺术博览会展期4天。2016年5月5日下午两点开始布展，5月6日开幕，5月9日闭幕。本届展会展厅分为四个展区：艺术品展\r\n区、艺术材料展区和艺术传媒展区、藏品展区。参展机构包括艺术协会、学会、研究会、画院、美术馆、画廊、艺术品公司、拍卖公司、艺术团体、艺术村落和社\r\n区、艺术教育（各级各类艺术学校、学院、大学）；参展作品包括国画、油画、版画、雕塑、书法、篆刻、素描、水彩、水粉、影像艺术、装置艺术等；参展艺术材\r\n料包括文房四宝、装裱工程工艺、艺术耗材等；参展艺术传媒包括艺术报刊、艺术出版物、艺术新媒体等；参展藏品包括瓷器、玉器、雕品、漆器、珠宝、翡翠、紫\r\n砂、织绣艺术等，涵盖范围广泛，呈现形式丰富。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		李杰透露，本届展会期间，主办方将举办中国巴蜀艺术金融发展论坛，论坛将邀请国内知名艺术金融专家和金融家就艺术与金融的融合发展发表自己的独到见解，\r\n为艺术金融的发展方向把脉引航。同时将开展巴蜀艺博会开幕酒会暨媒体见面会、巴蜀艺博会投资收藏讲座、巴蜀艺博会宝墩春季拍卖会等活动，宝墩春季拍卖会以\r\n一平尺的作品和扇面作品为主，旨在拉近消费者和艺术品市场的距离，让更多市民可以走进上乘的艺术品。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<span style=\"color:#C00000;\"><strong>服务大众：推动文化艺术深入人民群众</strong></span>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		李杰介绍说：“我们希望艺博会是大家参与、介入的艺术盛会，组委会把巴蜀艺博会定位于为提供艺术品展示和交易的平台，不仅关注展陈效果，还关注交易成\r\n果。所以要求参展艺术品具备吸引市场的特点。希望艺博会更加亲民、接地气，能兼顾学术、艺术品市场、大众收藏等方面，让巴蜀艺博会成为大众喜好的艺博\r\n会。”\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		2016巴蜀国际艺术博览会与其他文博会和艺博会的最大不同在于，2016巴蜀国际艺术博览会重在推动文化艺术产业的金融市场化转型，大力引导具有市场号召力的艺术作品，实现艺术品走进人民、服务群众的最终目的。\r\n	</p>\r\n</p>', null, null, '1', '由中共四川省委宣传部、四川省文化厅、四川省文联、四川省商务厅、四川博览事务局、成都市博览局指导，四川省诗书画院、四川省美术家协会、四川省书法家协会支持，中华美网、四川省巴蜀画派促进会、成都国家广告产业园联合主办，四川巴蜀画派文化产业集团承办，四川宝墩拍卖有限公司、四川宝墩艺术品有限公司协办的2016巴蜀国际艺术博览会将于2016年5月6日至9日在成都市世纪城新国际会展中心隆重举行。', '1428681600', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('51', '巴蜀画派文化产业集团董事长李杰 荣膺“2015中国改革十大创新人物”', '<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826135556_11576.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	12月27日，以“机遇、责任、创新”为主题的2015中国改革高峰论坛暨第十四届中国改革人物颁奖典礼在北京钓鱼台国宾馆隆重举行。巴蜀画派文化产业集团董事长李杰获“2015中国改革十大创新人物”殊荣。本次活动由<a href=\"http://zhonghuameiwang.com/artist/56.shtml\" target=\"_blank\">国家</a>发展改革委主管，中国经济导报社、<a href=\"http://zhonghuameiwang.com/artist/56.shtml\" target=\"_blank\">国家</a>发展改革委中国经贸导刊杂志社、CCTV—发现之旅、人民网、新华网、中国新闻网等机构主办和协办。\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826135556_16061.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<p style=\"font-size:14px;\">\r\n		第十一届全国人大常委、财经委副主任贺铿，<a href=\"http://zhonghuameiwang.com/artist/56.shtml\" target=\"_blank\">国家</a>发展改革委中国经济导报社社长王平生，国务院参事室特约研究员、<a href=\"http://zhonghuameiwang.com/artist/56.shtml\" target=\"_blank\">国家</a>统计局原总经济师、著名经济学家姚景源，<a href=\"http://zhonghuameiwang.com/artist/56.shtml\" target=\"_blank\">国家</a>发展改革委中国经贸导刊杂志社副社长赵泓，<a href=\"http://zhonghuameiwang.com/artist/56.shtml\" target=\"_blank\">国家</a>发展改革委顾问办研究员周君，亚洲品牌协会执行主席贾明辉，中国农学会原副会长孙翔，中国改革人物征评活动组委会秘书长王建功等领导与专家受邀出席论坛和颁奖。\r\n	</p>\r\n	<p style=\"font-size:14px;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;\">\r\n		本届中国改革人物评选始终坚持秉承一贯宗旨：“弘扬改革业绩、讴歌改革人物”，通过潜心梳理，从政界、知识界、经济领域、农村领域遴选出一些担任不同角\r\n色的人物和标志性的事件，以公开、公正、公平的原则，依据创新力、影响力、贡献力、发展力、经营力等指标，由专家评审委员会进行评选。获奖人物包括：海尔\r\n集团董事局副主席、轮值总裁周云杰，联想集团中国区总裁童夫尧，中国华信能源有限公司执行董事李勇，读者出版传媒股份有限公司董事长、总编辑吉西平，北京\r\n新发地农产品批发市场董事长张玉玺等\r\n	</p>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826135556_64788.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<p style=\"font-size:14px;\">\r\n		据悉，本届评选活动，巴蜀画派文化产业集团是四川唯一的获奖单位。\r\n	</p>\r\n	<p style=\"font-size:14px;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;\">\r\n		巴蜀画派自2009年起筹备创建，历经七年，在省委宣传部的领导和省文联的业务指导下，巴蜀画派重视梳理学术内涵和市场价值，求大同存小异，明确了“巴\r\n蜀画派”在中国美术界的价值和地位。2011年1月，中国美术家协会主席刘大为在媒体上呼吁：“推广巴蜀画派，刻不容缓”。“推进巴蜀画派建设”、“扶持\r\n巴蜀画派”先后被列入省委九届九次全委会关于文化强省战略的纲领性文件和四川省第十二个五年发展规划纲要。<span style=\"font-size:12px;line-height:1.5;\"></span>\r\n	</p>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826135556_34211.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<p style=\"font-size:14px;\">\r\n		“创\r\n建巴蜀画派”七年来，全国和省级主流和专业媒体报道的篇（次）累计1000多篇。百度、搜狗等搜索词条数量达到800多万条。自办《巴蜀画派》杂志、《中\r\n华美术》杂志、《中华美术报》、宝墩艺术品微信以及全国主流画派的门户网“中华美网”，组成“中国艺术媒体联盟”。其中，“中华美网”成为中国西部最大的\r\n艺术门户网。\r\n	</p>\r\n	<p style=\"font-size:14px;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;\">\r\n		“创建巴蜀画派”七年来，巴蜀画派在四川的国宾馆金牛宾馆举办了“首届巴蜀画派艺术品鉴会”，见证了巴蜀画派260名书画艺术家分别授予卓越成就代表人\r\n物、影响力代表人物和艺术实力代表人物的诞生。在北京中国军事博物馆和成都世纪新会展中心分别举办了“四川更加美丽——四川美术书法名家作品展”大型展\r\n览，200多位巴蜀画派艺术家的作品从艺术的角度，展示了四川人民抗震救灾3周年取得的伟大成就，诠释了四川人民从悲壮走向豪迈的抗震救灾精神。2013\r\n年6月，巴蜀画派促进会被“财富全球论坛组委会”授予“唯一艺术品合作单位”，巴蜀画派的15位艺术家用艺术作品与世界政要和世界500强企业家领袖的精\r\n彩对话，有力的推广了巴蜀画派的国际知名度和影响力。2014年9月，全国当代画派联谊会在沈阳举办第三届画派年会，巴蜀画派派出了名誉会长<a href=\"http://zhonghuameiwang.com/artist/22.shtml\" target=\"_blank\">戴卫</a>，会长<a href=\"http://zhonghuameiwang.com/artist/9.shtml\" target=\"_blank\">马一平</a>，常务副会长李杰，以及副会长<a href=\"http://zhonghuameiwang.com/artist/21.shtml\" target=\"_blank\">罗云</a>、<a href=\"http://zhonghuameiwang.com/artist/33.shtml\" target=\"_blank\">陈志才</a>、<a href=\"http://zhonghuameiwang.com/artist/24.shtml\" target=\"_blank\">陈仕彬</a>和副秘书长<a href=\"http://zhonghuameiwang.com/artist/29.shtml\" target=\"_blank\">孟夏</a>等\r\n组成的强大阵容参会，巴蜀画派被排在全国主流画派的第一位置呈现。2015年5月，巴蜀画派集团成功举办“中国巴蜀国际艺术博览会”和“中国巴蜀艺术金融\r\n发展论坛”，不仅收获了巴蜀国际艺博会的成功举办，赢得了世界各地艺术家的广泛关注，而且进一步证明了巴蜀画派在全国艺术届的影响力。\r\n	</p>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826135556_40574.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<p style=\"font-size:14px;\">\r\n		“创建巴蜀画派”七年来，先后举办各种展览活动和作品研讨会，到市州举办当地画家集体展览，为市州艺术发展授牌“巴蜀画派创作基地”。分别组织书画家，\r\n到机关企事业单位采风创作，到全国各地进行学习交流。到英国皇家美术学院、伦敦当代艺术中心等国外艺术机构参观交流，接待苏富比研究中心、香港亚洲艺术节\r\n组委会等艺术机构交流访问。\r\n	</p>\r\n	<p style=\"font-size:14px;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;\">\r\n		“创建巴蜀画派”七年来，组织开展“巴蜀画派名家进校园”活动，分别到中小学校与师生面对面交流，开展“巴蜀小画家培训工程”，组织“巴蜀小画家教师培\r\n训认证工程”，开展“成都市中小学生艺术社会实践活动”。同时，在艺术品市场交易方面，做出了大胆的探索，组建了宝墩艺术品公司和宝墩拍卖公司，在互联\r\n网＋时代，构建了艺术品电商线上线下的交易渠道，让艺术品成为更多社会主流人群的投资理财和居家装饰选择。\r\n	</p>\r\n	<p style=\"font-size:14px;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;\">\r\n		“创建巴蜀画派”七年来，巴蜀画派一直秉承“责任与传承”的重要理念，响亮地提出“源于古蜀文明，影响中国美术，光耀世界画坛”的推广理念。\r\n	</p>\r\n<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826135556_97201.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<p style=\"font-size:14px;\">\r\n		2015年9月，四川省工商联和四川日报报业集团指导，川商杂志社和川商全国理事会评选授予巴蜀画派文化产业集团董事长李杰“2015杰出青年川商奖”，同时，李杰当选为“全国青年川商联席会副会长”。\r\n	</p>\r\n	<p style=\"font-size:14px;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;\">\r\n		2015年11月，四川省民政厅和四川省文联授予四川省巴蜀画派促进会常务副会长李杰“2015四川省社会组织先进个人”称号。\r\n	</p>\r\n	<p style=\"font-size:14px;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;\">\r\n		巴蜀画派文化产业集团董事长李杰荣获的这些荣誉和奖励，是各级党委政府部门对巴蜀画派创建七年来改革创新之路给予的充分肯定与鼓励，相信将更加激励巴蜀\r\n画派集团的全体同仁在向前发展的道路上，继续走在改革创新的前列，紧抓时代脉搏，艰苦奋斗，顽强拼搏，在推进中国文化产业改革和企业发展中做出卓越的成绩\r\n和贡献。\r\n	</p>\r\n</p>', null, null, '1', '12月27日，以“机遇、责任、创新”为主题的2015中国改革高峰论坛暨第十四届中国改革人物颁奖典礼在北京钓鱼台国宾馆隆重举行。巴蜀画派文化产业集团董事长李杰获“2015中国改革十大创新人物”殊荣。本次活动由国家发展改革委主管，中国经济导报社、国家发展改革委中国经贸导刊杂志社、CCTV—发现之旅、人民网、新华网、中国新闻网等机构主办和协办。第十一届全国人大常委、财经委副主任贺铿，国家发展改革委中国经济导报社社长王平生，国务院参事室特约研究员、国家统计局原总经济师、著名经济学家姚景源，国家发展改革委中国经贸导刊杂志社副社长赵泓，国家发展改革委顾问办研究员周君，亚洲品牌协会执行主席贾明辉，中国农学会原副会长孙翔，中国改革人物征评活动组委会秘书长王建功等领导与专家受邀出席论坛和颁奖。', '1428595200', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('52', '2015宝墩秋拍圆满落槌 巴蜀书画作品成交率82.8% 2016宝墩春拍将于5月举槌', '<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:24px;\"><strong>2015宝墩秋拍圆满落槌</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<span style=\"color:#FF0000;font-size:18px;\">巴蜀书画作品成交率82.8%</span>\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<span style=\"color:#FF0000;font-size:18px;\"><strong>2016宝墩春拍将于5月举槌</strong></span><span style=\"font-size:14px;line-height:1.5;\"></span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826140714_59012.png\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	2015宝墩秋季拍卖会于12月13日下午5点在成都浣花黉台酒店圆满落槌。本次拍卖会推出26位巴蜀画派知名艺术家共49幅力作亮相，作品成交率达到82.8%，为藏家和收藏爱好者带来超值的书画艺术收藏盛宴。\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826140714_13015.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:left;font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	本次拍卖分“生于40”和“巴蜀影响力名家”两个主题板块隆重登场。拍卖汇集了上世纪40年代出生的部分巴蜀艺术家：<a href=\"http://zhonghuameiwang.com/artist/9.shtml\" target=\"_blank\">马一平</a>、<a href=\"http://zhonghuameiwang.com/artist/170.shtml\" target=\"_blank\">何哲生</a>、<a href=\"http://zhonghuameiwang.com/artist/34.shtml\" target=\"_blank\">张文源</a>、<a href=\"http://zhonghuameiwang.com/artist/125.shtml\" target=\"_blank\">王龙生</a>、<a href=\"http://zhonghuameiwang.com/artist/126.shtml\" target=\"_blank\">简崇民</a>、<a href=\"http://zhonghuameiwang.com/artist/128.shtml\" target=\"_blank\">徐光弟</a>、<a href=\"http://zhonghuameiwang.com/artist/176.shtml\" target=\"_blank\">简崇志</a>；以及巴蜀影响力名家：<a href=\"http://zhonghuameiwang.com/artist/168.shtml\" target=\"_blank\">贾兴桐</a>、张跃进、<a href=\"http://zhonghuameiwang.com/artist/195.shtml\" target=\"_blank\">杨梁相</a>、<a href=\"http://zhonghuameiwang.com/artist/15.shtml\" target=\"_blank\">何冠霖</a>、东方腾弘、林泉、<a href=\"http://zhonghuameiwang.com/artist/133.shtml\" target=\"_blank\">刘遂海</a>、<a href=\"http://zhonghuameiwang.com/artist/19.shtml\" target=\"_blank\">米金铭</a>、巫成金、<a href=\"http://zhonghuameiwang.com/artist/38.shtml\" target=\"_blank\">胡峻涤</a>、<a href=\"http://zhonghuameiwang.com/artist/174.shtml\" target=\"_blank\"></a><a href=\"http://zhonghuameiwang.com/artist/152.shtml\" target=\"_blank\">余长明</a>、赵建华、<a href=\"http://zhonghuameiwang.com/artist/33.shtml\" target=\"_blank\">陈志才</a>、<a href=\"http://zhonghuameiwang.com/artist/21.shtml\" target=\"_blank\">罗云</a>、李昌彦、夏维明、谭晓明、<a href=\"http://zhonghuameiwang.com/artist/40.shtml\" target=\"_blank\">霍晓</a>、黄芸芸等书画家的力作。\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826140715_21060.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	四\r\n川宝墩拍卖有限公司首次拍卖亮相，精心选择了巴蜀画派知名艺术家的作品，在新老藏家持续不断的热情支持与期盼中，宝墩拍卖携手四川省巴蜀画派促进会邀请到\r\n巴蜀地区老中青三代艺术家鼎力加盟，其作品不仅题材广泛、特色鲜明，而且具有深厚的艺术价值，极具市场升值空间和较高的收藏价值。<span style=\"font-size:14px;line-height:1.5;\"></span>\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826140715_75280.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	在中国经济新常态的大背景下，拍卖市场每每传出佳绩，给中国经济增添了一抹亮丽的色彩。巴蜀画派在国内外影响力逐步提升的今天，宝墩拍卖为藏家和收藏爱好者奉献的此次艺术盛宴，从作品质量、市场潜力、升值空间等方面都倍受国内藏家看好和追捧。\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826140715_15759.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:left;font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		四\r\n川宝墩拍卖有限公司有关负责人表示，宝墩拍卖是巴蜀画派集团的全资子公司，将承担巴蜀画派各位艺术家的市场拍卖工作。拍卖作品以“求真、求实”为准则，致\r\n力营造健康、诚信的艺术品交易市场。宝墩拍卖将用完全保真的作品来服务好各位藏家，以感谢对巴蜀画派艺术家的支持和厚爱。相信本次宝墩拍卖的成功落槌及对\r\n求真的坚持，将培养更多的藏家，给巴蜀画派艺术家的潜心创作带来信心和创作源泉，形成艺术市场的良性循环，推动巴蜀画派在中国艺术市场的健康发展。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		据悉，2016宝墩艺术品春季拍卖会将于2016年5月6日开幕的第二届巴蜀国际艺博会期间在成都举行。\r\n	</p>\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826140715_87398.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:center;background-color:#FFFFFF;\">\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		针\r\n对目前的书画艺术品收藏市场，记者采访巴蜀画派促进会常务副会长兼秘书长李杰教授时，他认为，由于受到整体经济状况的影响，艺术品市场行情随之不太景气，\r\n但是，书画艺术品的收藏在这个阶段呈现出的特点不容忽视：一是出现了价格大幅下降的优秀作品，这是过去价格高峰时不可想象的；二是出现了真正意义上的藏\r\n家，他们懂艺术懂市场有实力；三是优秀书画艺术品的收藏潜力毋庸置疑，这是其它收藏和理财产品无法比拟的。同时，李杰教授表示，相信书画艺术品市场一定会\r\n走向健康可持续的发展道路，带给人们开心快乐的艺术享受。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;text-align:right;background-color:#FFFFFF;\">\r\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 中华美网记者/徐世冲\r\n	</p>\r\n</p>', null, null, '2', '2015宝墩秋季拍卖会于12月13日下午5点在成都浣花黉台酒店圆满落槌。本次拍卖会推出26位巴蜀画派知名艺术家共49幅力作亮相，作品成交率达到82.8%，为藏家和收藏爱好者带来超值的书画艺术收藏盛宴。本次拍卖分“生于40”和“巴蜀影响力名家”两个主题板块隆重登场。拍卖汇集了上世纪40年代出生的部分巴蜀艺术家：马一平、何哲生、张文源、王龙生、简崇民、徐光弟、简崇志；以及巴蜀影响力名家：贾兴桐、张跃进、杨梁相、何冠霖、东方腾弘、林泉、刘遂海、米金铭、巫成金、胡峻涤、余长明、赵建华、陈志才、罗云、李昌彦、夏维明、谭晓明、霍晓、黄芸芸等书画家的力作。', '1428508800', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('53', '“生于40巴蜀油画暨巴蜀影响力当代书画展”12月5日在川博圆满开幕', '<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826141329_37237.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	【中华美网讯】由中共四川省委宣传部、四川省文联指导，四川博物院和四川省美术家协会支持，四川省巴蜀画派促进会主办的“生于40巴蜀油画暨巴蜀影响力当\r\n代书画展”于12月5日上午在四川博物院圆满开幕。四川省政府副秘书长、办公厅主任蔡竞，新华社四川分社主任蒋作平，成都市青羊区委常委邱颖，成都<a href=\"http://zhonghuameiwang.com/artist/56.shtml\" target=\"_blank\">国家</a>广告产业园董事长刘移，西南民族大学艺术学院院长穆兰等领导和嘉宾出席了开幕式。艾轩、<a href=\"http://zhonghuameiwang.com/artist/9.shtml\" target=\"_blank\">马一平</a>、<a href=\"http://zhonghuameiwang.com/artist/22.shtml\" target=\"_blank\">戴卫</a>、<a href=\"http://zhonghuameiwang.com/artist/167.shtml\" target=\"_blank\">高小华</a>、<a href=\"http://zhonghuameiwang.com/artist/166.shtml\" target=\"_blank\">刘朴</a>、<a href=\"http://zhonghuameiwang.com/artist/139.shtml\" target=\"_blank\"></a><a href=\"http://zhonghuameiwang.com/artist/140.shtml\" target=\"_blank\">梁时民</a>、<a href=\"http://zhonghuameiwang.com/artist/170.shtml\" target=\"_blank\">何哲生</a>、<a href=\"http://zhonghuameiwang.com/artist/34.shtml\" target=\"_blank\">张文源</a>、<a href=\"http://zhonghuameiwang.com/artist/125.shtml\" target=\"_blank\">王龙生</a>、<a href=\"http://zhonghuameiwang.com/artist/126.shtml\" target=\"_blank\">简崇民</a>、何多苓、<a href=\"http://zhonghuameiwang.com/artist/128.shtml\" target=\"_blank\">徐光弟</a>、<a href=\"http://zhonghuameiwang.com/artist/176.shtml\" target=\"_blank\">简崇志</a>、<a href=\"http://zhonghuameiwang.com/artist/168.shtml\" target=\"_blank\">贾兴桐</a>、张跃进、<a href=\"http://zhonghuameiwang.com/artist/15.shtml\" target=\"_blank\">何冠霖</a>、<a href=\"http://zhonghuameiwang.com/artist/33.shtml\" target=\"_blank\">陈志才</a>、<a href=\"http://zhonghuameiwang.com/artist/21.shtml\" target=\"_blank\">罗云</a>、杨梁相、<a href=\"http://zhonghuameiwang.com/artist/133.shtml\" target=\"_blank\">刘遂海</a>、<a href=\"http://zhonghuameiwang.com/artist/19.shtml\" target=\"_blank\">米金铭</a>、巫成金、<a href=\"http://zhonghuameiwang.com/artist/38.shtml\" target=\"_blank\">胡峻涤</a>、<a href=\"http://zhonghuameiwang.com/artist/174.shtml\" target=\"_blank\"></a><a href=\"http://zhonghuameiwang.com/artist/152.shtml\" target=\"_blank\">余长明</a>、赵建华、东方腾弘、林泉、李昌彦、夏维明、谭晓明、<a href=\"http://zhonghuameiwang.com/artist/40.shtml\" target=\"_blank\">霍晓</a>、黄芸芸等众多艺术家到场为此次画展造势。\r\n</p>\r\n<p style=\"text-align:left;\">\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826141329_58301.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	开幕式现场\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	巴蜀画派促进会常务副会长兼秘书长李杰主持展览开幕式并讲话。 \r\n他说：“生于40是巴蜀画派促进会最近两年一直在《巴蜀画派》杂志上做的选题，推出选题后受到读者和市场的普遍欢迎，于是，我们策划了集中展示40年代巴\r\n蜀油画家作品的主题展。同时，巴蜀画派作品在当代呈现欣欣向荣的发展势头，尤其是在德高望重的老艺术家<a href=\"http://zhonghuameiwang.com/artist/22.shtml\" target=\"_blank\">戴卫</a>、<a href=\"http://zhonghuameiwang.com/artist/166.shtml\" target=\"_blank\">刘朴</a>等老师的带领下，巴蜀画派影响力日益提升，成为中国艺术市场不得不关注和研究的重要群体。”\r\n</p>\r\n<p style=\"text-align:left;\">\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826141329_40945.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	巴蜀画派促进会常务副会长兼秘书长李杰主持展览开幕式\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	川音成都美术学院教授、第一批巴蜀画派影响力代表人物<a href=\"http://zhonghuameiwang.com/artist/125.shtml\" target=\"_blank\">王龙生</a>给\r\n大家分享了对于此次展览的感受：“我非常高兴能够来参加此次展览，因为这个展览的选题是生于40，我觉得这个选题非常好，而且在这么多过去的展览中这样的\r\n选题还当属第一次，参加此次展览的老艺术家们对于举办这样的一个展览都感到特别的欣慰。在当代已艺术崛起的年代，传统艺术和文化处于有些边缘的状态，但是\r\n这一次专门把这些老艺术家的作品聚集起来策划了这样的一个展览，对于我们以及传统艺术来说都是特别有益的一个活动。另外，我想说说巴蜀画派，政府花了这么\r\n大的财力和物力来打造这样的一个属于我们自己的品牌，必是有一定的原因的，而我们的巴蜀画派这几年的发展也不负众望，希望推广巴蜀画派不仅仅是在成都进\r\n行，而是要深入的扩展到全国，甚至全世界！\r\n</p>\r\n<p style=\"text-align:left;\">\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826141329_71523.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	川音成都美术学院教授、第一批巴蜀画派影响力代表人物<a href=\"http://zhonghuameiwang.com/artist/125.shtml\" target=\"_blank\">王龙生</a>发言\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		作为此次展览特邀展览画家，<a href=\"http://zhonghuameiwang.com/artist/167.shtml\" target=\"_blank\">高小华</a>老\r\n师以调侃的方式对此次展览以及巴蜀画派表达了自己的祝福：“我不是40年代的，实际上用现在流行的话来讲，我算是55后，我跟40年代有着不可分割的血缘\r\n关系，我的老哥们儿、我的师长、我的师兄们都是这个年代出生的卓越的老艺术家，今天能出席这个展览开幕式我感到特别的高兴，我很荣幸能够和这些艺术圈里的\r\n老艺术家们一同参加这个展览，也特别感谢巴蜀画派给我们提供这样一个精彩的展览。”<span style=\"font-family:\'sans serif\', tahoma, verdana, helvetica;font-size:12px;line-height:1.5;\"></span>\r\n	</p>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826141329_89961.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	巴蜀画派促进会常务副会长兼秘书长李杰与嘉宾合影\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		新华社四川分社主任蒋作平表达了自己对于艺术家们的敬意以及对于展览作品的赞赏。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		四川省政府副秘书长、办公厅主任蔡竞宣布此次展览正式开幕。开幕式结束后，领导、嘉宾、艺术家和群众一同观展，并对展出的作品表达了充分的肯定。此次展览将持续到12月13日。\r\n	</p>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826141330_95775.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	展览现场人潮涌动\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	据悉，由于本次展出作品倍受国内藏家看好和追捧，主办方将考虑将其中的部分优秀作品进行拍卖，以回馈藏家的热情期待，支持文化艺术的公益事业发展，拍卖时间为12月13日下午2点在成都浣花黉台酒店举行。\r\n</p>\r\n<p style=\"text-align:left;\">\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826141330_29044.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	特邀展览画家<a href=\"http://zhonghuameiwang.com/artist/167.shtml\" target=\"_blank\">高小华</a>发言\r\n</p>', null, null, '2', '【中华美网讯】由中共四川省委宣传部、四川省文联指导，四川博物院和四川省美术家协会支持，四川省巴蜀画派促进会主办的“生于40巴蜀油画暨巴蜀影响力当代书画展”于12月5日上午在四川博物院圆满开幕。四川省政府副秘书长、办公厅主任蔡竞，新华社四川分社主任蒋作平，成都市青羊区委常委邱颖，成都国家广告产业园董事长刘移，西南民族大学艺术学院院长穆兰等领导和嘉宾出席了开幕式。艾轩、马一平、戴卫、高小华、刘朴、梁时民、何哲生、张文源、王龙生、简崇民、何多苓、徐光弟、简崇志、贾兴桐、张跃进、何冠霖、陈志才、罗云、杨梁相、刘遂海、米金铭、巫成金、胡峻涤、余长明、赵建华、东方腾弘、林泉、李昌彦、夏维明、谭晓明、霍晓、黄芸芸等众多艺术家到场为此次画展造势。', '1428422400', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('54', '生于40巴蜀油画暨巴蜀影响力当代书画展 明日(12月5日)在四川博物院开幕', '<p>\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826142138_86352.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	（中\r\n华美网讯）由中共四川省委宣传部、四川省文联指导，四川博物院和四川省美术家协会支持，四川省巴蜀画派促进会主办的“生于40巴蜀油画暨巴蜀影响力当代书\r\n画展”明日上午10点在四川博物院（临展1厅）开幕。届时，名家荟萃，精彩纷呈，国画、书法、油画作品一一登场，展览时间将持续至12月13日。\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	本次展览将展出上世纪40年代出生的部分油画家艾轩、<a href=\"http://zhonghuameiwang.com/artist/9.shtml\" target=\"_blank\">马一平</a>、<a href=\"http://zhonghuameiwang.com/artist/170.shtml\" target=\"_blank\">何哲生</a>、<a href=\"http://zhonghuameiwang.com/artist/34.shtml\" target=\"_blank\">张文源</a>、<a href=\"http://zhonghuameiwang.com/artist/125.shtml\" target=\"_blank\">王龙生</a>、<a href=\"http://zhonghuameiwang.com/artist/126.shtml\" target=\"_blank\">简崇民</a>、何多苓、<a href=\"http://zhonghuameiwang.com/artist/128.shtml\" target=\"_blank\">徐光弟</a>、<a href=\"http://zhonghuameiwang.com/artist/176.shtml\" target=\"_blank\">简崇志</a>的代表新作以及巴蜀当代美术名家<a href=\"http://zhonghuameiwang.com/artist/22.shtml\" target=\"_blank\">戴卫</a>、<a href=\"http://zhonghuameiwang.com/artist/167.shtml\" target=\"_blank\">高小华</a>、<a href=\"http://zhonghuameiwang.com/artist/168.shtml\" target=\"_blank\">贾兴桐</a>、<a href=\"http://zhonghuameiwang.com/artist/166.shtml\" target=\"_blank\">刘朴</a>、<a href=\"http://zhonghuameiwang.com/artist/139.shtml\" target=\"_blank\"></a><a href=\"http://zhonghuameiwang.com/artist/140.shtml\" target=\"_blank\">梁时民</a>、张跃进、<a href=\"http://zhonghuameiwang.com/artist/15.shtml\" target=\"_blank\">何冠霖</a>、<a href=\"http://zhonghuameiwang.com/artist/33.shtml\" target=\"_blank\">陈志才</a>、<a href=\"http://zhonghuameiwang.com/artist/21.shtml\" target=\"_blank\">罗云</a>、杨梁相、<a href=\"http://zhonghuameiwang.com/artist/133.shtml\" target=\"_blank\">刘遂海</a>、<a href=\"http://zhonghuameiwang.com/artist/19.shtml\" target=\"_blank\">米金铭</a>、巫成金、<a href=\"http://zhonghuameiwang.com/artist/38.shtml\" target=\"_blank\">胡峻涤</a>、<a href=\"http://zhonghuameiwang.com/artist/174.shtml\" target=\"_blank\"></a><a href=\"http://zhonghuameiwang.com/artist/152.shtml\" target=\"_blank\">余长明</a>、赵建华、东方腾弘、林泉、李昌彦、夏维明、谭晓明、<a href=\"http://zhonghuameiwang.com/artist/40.shtml\" target=\"_blank\">霍晓</a>、黄芸芸等32位艺术家的精品力作。特别邀请艾轩、何多苓、<a href=\"http://zhonghuameiwang.com/artist/167.shtml\" target=\"_blank\">高小华</a>携力作展出。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826142138_87706.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	工作人员布展\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		此\r\n次展览将展示有高水准艺术价值的艺术作品，积极表现巴蜀画派的精髓以及当代艺术家的深厚积淀与创新发展，参展作品既包括反映艺术家深刻思想的题材，也包括\r\n体现人文、自然与生活之美的创作。据悉，由于本次展出作品倍受国内藏家看好和追捧，主办方将考虑将其中的部分优秀作品进行拍卖以回馈藏家的热情期待。\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n</p>\r\n<p>\r\n	<img src=\"/Public/kindeditor/php/../../../Public/image/20160826/20160826142138_93268.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		生于40巴蜀油画暨巴蜀影响力当代书画展，许你一场丰富的艺术盛宴！\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong><span style=\"color:#C00000;\"><br />\r\n</span></strong>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong><span style=\"color:#C00000;\">展览时间：2015年12月5日—12月13日</span></strong>\r\n	</p>\r\n	<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n		<strong><span style=\"color:#C00000;\">展览地点：四川博物院（临展1厅）</span></strong>\r\n	</p>\r\n	<p>\r\n		<strong><br />\r\n</strong>\r\n	</p>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, null, '2', '（中华美网讯）由中共四川省委宣传部、四川省文联指导，四川博物院和四川省美术家协会支持，四川省巴蜀画派促进会主办的“生于40巴蜀油画暨巴蜀影响力当代书画展”明日上午10点在四川博物院（临展1厅）开幕。届时，名家荟萃，精彩纷呈，国画、书法、油画作品一一登场，展览时间将持续至12月13日。本次展览将展出上世纪40年代出生的部分油画家艾轩、马一平、何哲生、张文源、王龙生、简崇民、何多苓、徐光弟、简崇志的代表新作以及巴蜀当代美术名家戴卫、高小华、贾兴桐、刘朴、梁时民、张跃进、何冠霖、陈志才、罗云、杨梁相、刘遂海、米金铭、巫成金、胡峻涤、余长明、赵建华、东方腾弘、林泉、李昌彦、夏维明、谭晓明、霍晓、黄芸芸等32位艺术家的精品力作。特别邀请艾轩、何多苓、高小华携力作展出。', '1428336000', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('55', '四川省巴蜀画派促进会举行2015岁末老艺术家联谊会', '<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<span style=\"color:#AB1942;\"><strong><span style=\"font-size:14px;\"> 中华美网讯：</span></strong></span><span style=\"font-size:14px;\">&nbsp;\r\n	<p>\r\n		<span>11</span>月<span>19</span>日下午，四川省巴蜀画派促进会岁末老艺术家联谊会在四川博物院对外文化交流中心举行。出席此次联谊会的著名艺术家有戴卫、高小华、刘朴、谢季筠、沈道鸿、魏学峰、周明安、夏亮熹，因事或在外地无法出席联谊会的著名艺术家马一平、何应辉、彭先诚、郭汝愚、秦天柱、吴绪经、万启仁、袁生中也都通过电话或短信表达了未能与会的遗憾和对与会艺术家的诚挚祝福。<span></span>\r\n	</p>\r\n	<p>\r\n		在气氛和悦、温暖如春的联谊会现场，巴蜀画派促进会常务副会长兼秘书长李杰与老艺术家们畅谈了创建巴蜀画派七年、推广巴蜀画派五年来的总体工作，老艺术家们对巴蜀画派促进会在推广中表现出来的创新工作思维和方式给予了肯定，对巴蜀画派促进会在培育艺术教育、艺术品交易、艺术传媒三大产业板块中获得的成绩表示祝贺，对巴蜀画派未来的推广规划更是积极建言献策。<span></span>\r\n	</p>\r\n	<p>\r\n		<span>&nbsp;</span>\r\n	</p>\r\n	<p>\r\n		在谈到我国当前艺术品市场的现状时，艺术家们预测，随着近年来艺术品市场化进程不断推进、金融化程度的不断加深，中国艺术品市场虽然发展迅速，但依然不够成熟；时下的艺术创作环境还处在一个浮躁阶段，艺术作品呈现出<span>“</span>量多质低<span>”</span>的怪圈。尽管艺术创作的题材、技法等提倡百花齐放百家争鸣，但实质都源于火热的生活，艺术家们应该深入基层创作，找寻作品与心灵的共鸣，切实反应时代所需，在唱响时代主旋律的同时，肩负起一名中国艺术家应有的责任和担当，与时代同呼吸共命运。<span></span>\r\n	</p>\r\n	<p>\r\n		（记者 徐世冲）<span></span>\r\n	</p>\r\n</span>\r\n</p>', null, null, '1', '11月19日下午，四川省巴蜀画派促进会岁末老艺术家联谊会在四川博物院对外文化交流中心举行。出席此次联谊会的著名艺术家有戴卫、高小华、刘朴、谢季筠、沈道鸿、魏学峰、周明安、夏亮熹，因事或在外地无法出席联谊会的著名艺术家马一平、何应辉、彭先诚、郭汝愚、秦天柱、吴绪经、万启仁、袁生中也都通过电话或短信表达了未能与会的遗憾和对与会艺术家的诚挚祝福。', '1428249600', '1', null, '0', '0');
INSERT INTO `sp_news1111` VALUES ('56', '巴蜀画派受邀中国当代知名画派邀请展 66幅作品独领巴蜀风情', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div id=\"col_title\">\r\n	<div>\r\n	</div>\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"clear\">\r\n</div>\r\n<p>\r\n	<span style=\"font-family:\'microsoft yahei\', Arial;font-size:14px;line-height:1.5;color:#C00000;\">中华美网讯</span><span style=\"font-family:\'microsoft yahei\', Arial;font-size:14px;line-height:1.5;background-color:#FFFFFF;\">“中\r\n国精神·民族魂”——中国知名画派邀请展将于2015年10月30日—11月2日在重庆国际博览中心举办。据悉，本次展览是文化部和重庆市政府共同主办的\r\n第二届中国文化馆年会暨第五届重庆艺交会期间的重要活动。巴蜀画派作为国内知名画派，受邀参加作品展出和相关艺术活动。本次活动邀请的其他画派有：岭南画\r\n派、海上画派、黄土画派、关东画派、南昌画派、江西画派、巴渝画派、黄山画派、沂蒙画派、冰雪画派。据了解，巴蜀画派是本次参展阵容最强大、画作最多的画\r\n派。</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-family:\'microsoft yahei\', Arial;font-size:14px;line-height:1.5;background-color:#FFFFFF;\"><img src=\"/Public/image/20160826/20160826142941_68101.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"font-family:\'microsoft yahei\', Arial;font-size:14px;line-height:1.5;background-color:#FFFFFF;\"> </span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	巴\r\n蜀画派是从广义上来界定的，是指包括重庆在内的四川地域内，以历史悠久的巴蜀文化为依托，包括省内各少数民族在内的、从古至今的地区绘画流派的总称。巴蜀\r\n无论从地缘文化上还是艺术文化的界定上，都是最能代表四川美术艺术的，也体现了四川的艺术文化特色，具有鲜明的地域性和时代感。巴蜀画派是巴蜀文明和巴蜀\r\n文化的重要组成部分，是代表巴蜀美术人物和作品的集体符号，是地域特色鲜明的艺术符号，是中国的文化品牌，更是对外交流的文化名片。\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	巴蜀画派的创建与推广，一直备受四川省委省政府的高度重视。四川省委九届九次全委会在实施文化强省战略中明确提出“推进巴蜀画派建设”，四川省十一届人\r\n大四次会议批准将“扶持巴蜀画派”正式写入《四川省国民经济和社会发展第十二个五年规划纲要》；四川省委关于文化强省战略提出文化进军号：“巴蜀风格，中\r\n国气派”。2011年1月，中国美术家协会主席刘大为在媒体上呼吁：“推广巴蜀画派，刻不容缓”。2014年8月30日，中共中央政治局委员、中宣部部长\r\n刘奇葆在中国美术馆观看四川省诗书画院建院30周年展览时，特别对在场的四川书画家发出号召，他指出“创建巴蜀画派，大家一起努力！”\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	本次赴渝展览，巴蜀画派促进会组织58名著名艺术家的66幅作品参加，其中包括国画作品33幅、油画作品18幅、书法作品15幅。巴蜀画派促进会名誉会长<a href=\"http://www.zhonghuameiwang.com/artist/22.shtml\" target=\"_blank\">戴卫</a>先生虽然出差外地，但他特别委托常务副会长李杰转告对中国当代画派联谊会会长<a href=\"http://www.zhonghuameiwang.com/artist/96.shtml\" target=\"_blank\">刘文西</a>先生的问候，特别祝贺本次展览和艺术交流活动取得圆满成功；巴蜀画派促进会会长<a href=\"http://www.zhonghuameiwang.com/artist/9.shtml\" target=\"_blank\">马一平</a>先生也因重要教学事务无法赴渝参加现场活动，但对巴蜀画派的作品展览特别关心和给予指导。届时，四川省巴蜀画派促进会常务副会长兼秘书长李杰，四川省巴蜀画派促进会副会长、成都市工笔画会常务副会长<a href=\"http://www.zhonghuameiwang.com/artist/33.shtml\" target=\"_blank\">陈志才</a>，四川省巴蜀画派促进会副会长、成都市美术家协会副主席<a href=\"http://www.zhonghuameiwang.com/artist/19.shtml\" target=\"_blank\">米金铭</a>，四川省巴蜀画派促进会副会长、西南民族大学艺术学院教授<a href=\"http://www.zhonghuameiwang.com/artist/144.shtml\" target=\"_blank\">刘学伦</a>，四川省书法家协会副主席、四川省巴蜀画派促进会副会长、四川省文化馆馆长助理<a href=\"http://www.zhonghuameiwang.com/artist/27.shtml\" target=\"_blank\">林峤</a>等艺术家将亲临活动现场，参加各项活动。巴蜀画派促进会在重庆的展览，将全面和独立展示巴蜀画派的独特艺术风格与重大成果，并和其它画派进行学术交流，为促进中国当代画派的健康发展与艺术繁荣做出不懈努力。\r\n</p>\r\n<p style=\"font-size:14px;font-family:\'microsoft yahei\', Arial;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<table>\r\n	<tbody>\r\n		<tr class=\"firstRow\">\r\n			<td colspan=\"6\" valign=\"top\" width=\"553\">\r\n				<p style=\"font-size:14px;text-align:center;\">\r\n					<strong><span style=\"color:#938953;\">巴蜀画派参展作品名单</span></strong> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td valign=\"top\" width=\"53\">\r\n				<p style=\"font-size:14px;\">\r\n					序号\r\n				</p>\r\n			</td>\r\n			<td valign=\"top\" width=\"55\">\r\n				<p style=\"font-size:14px;\">\r\n					类别\r\n				</p>\r\n			</td>\r\n			<td valign=\"top\" width=\"130\">\r\n				<p style=\"font-size:14px;\">\r\n					名称\r\n				</p>\r\n			</td>\r\n			<td valign=\"top\" width=\"85\">