CREATE TABLE `sp_product` (
  `p_id` tinyint(4) NOT NULL AUTO_INCREMENT,
  `p_title` varchar(255) NOT NULL COMMENT '拍卖名',
  `p_info` varchar(255) NOT NULL COMMENT '拍卖详情',
  `p_intro` varchar(255) NOT NULL COMMENT '拍卖参数(私有参数）',
  `p_pics` varchar(255) NOT NULL COMMENT '拍卖图片展示',
  `p_time` int(11) NOT NULL,
  `p_num` varchar(255) NOT NULL COMMENT '标的物 编号',
  `p_count` varchar(255) NOT NULL COMMENT '预约人数(填会员id）',
  `p_price` int(11) unsigned NOT NULL COMMENT '起拍价',
  `p_ytime` int(11) unsigned NOT NULL COMMENT '拍卖时间',
  `p_area` int(11) unsigned NOT NULL COMMENT '所在地（填写地点id）',
  `p_pmpt` varchar(60) NOT NULL COMMENT '拍卖平台',
  `p_kind` int(11) unsigned NOT NULL COMMENT '标的物类型（填写类型id）',
  `p_fy` int(11) unsigned NOT NULL COMMENT '所属法院（填写法院id）',
  PRIMARY KEY (`p_id`)
) ENGINE=MyISAM AUTO_INCREMENT=21 DEFAULT CHARSET=utf8;

